import React, { useEffect, useState } from 'react';

// Floating rings background animation with fixed positions
const FloatingRingsBackground = ({ darkMode }) => {
  const [rings, setRings] = useState([]);
  
  useEffect(() => {
    // Generate evenly distributed ring elements with fixed positions
    const generateRings = () => {
      // Define a fixed set of positions for rings (evenly distributed)
      const fixedPositions = [
        { x: 20, y: 20 },  // Top left
        { x: 50, y: 20 },  // Top center
        { x: 80, y: 20 },  // Top right
        { x: 20, y: 50 },  // Middle left
        { x: 50, y: 50 },  // Center
        { x: 80, y: 50 },  // Middle right
        { x: 20, y: 80 },  // Bottom left
        { x: 50, y: 80 },  // Bottom center
        { x: 80, y: 80 },  // Bottom right
      ];
      
      const ringsCount = window.innerWidth < 768 ? 5 : 9;
      const newRings = [];
      
      for (let i = 0; i < ringsCount; i++) {
        // Use fixed positions for even distribution
        const position = fixedPositions[i % fixedPositions.length];
        
        // Calculate random properties for each ring
        const size = 150 + (i * 25) % 250; // Size between 150px and 400px, deterministic but varied
        const thickness = 50 + (i * 10) % 75; // Thickness between 50px and 125px
        
        // Fixed movement directions that alternate
        const xDirection = i % 2 === 0 ? 1 : -1;
        const yDirection = i % 3 === 0 ? 1 : -1;
        
        // Fixed durations but varied
        const duration = 20 + (i * 3) % 25; // Animation duration between 20s and 45s
        
        // Fixed distances but varied
        const xDistance = 10 + (i * 2) % 20; // Move 10-30% horizontally
        const yDistance = 10 + (i * 2) % 20; // Move 10-30% vertically
        
        // No delays
        const delay = 0;
        
        // Fixed gradient angles but varied
        const gradientAngle = (i * 45) % 360;
        
        newRings.push({
          id: i,
          size,
          x: position.x,
          y: position.y,
          thickness,
          xDirection,
          yDirection,
          duration,
          xDistance,
          yDistance,
          delay,
          gradientAngle
        });
      }
      
      setRings(newRings);
    };
    
    generateRings();
    
    // Regenerate on window resize to maintain proper sizing
    const handleResize = () => {
      generateRings();
    };
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className="rings-animation-container absolute inset-0 overflow-hidden z-0" style={{ pointerEvents: 'none' }}>
      {rings.map((ring) => (
        <div
          key={ring.id}
          className="absolute rounded-full"
          style={{
            width: `${ring.size}px`,
            height: `${ring.size}px`,
            // Use simple border for maximum compatibility
            border: `${ring.thickness}px solid`,
            borderColor: getRingColor(ring.id, darkMode),
            // Make the background transparent to create a hollow ring
            background: 'transparent',
            // Initial position
            left: `calc(${ring.x}% - ${ring.size / 2}px)`,
            top: `calc(${ring.y}% - ${ring.size / 2}px)`,
            // Increased opacity for better visibility
            opacity: 0.6,
            // Animation with no delay
            animation: `float-${ring.id} ${ring.duration}s ease-in-out infinite`,
          }}
        />
      ))}
      
      {/* Create keyframe animations for each ring */}
      <style>
        {rings.map((ring) => `
          @keyframes float-${ring.id} {
            0%, 100% {
              transform: translate(0, 0);
            }
            50% {
              transform: translate(
                ${ring.xDirection * ring.xDistance}%, 
                ${ring.yDirection * ring.yDistance}%
              );
            }
          }
        `).join('')}
      </style>
    </div>
  );
};

// Function to get ring color - alternating between lime and green shades
function getRingColor(index, darkMode) {
  const limeColor = 'rgba(132, 204, 22, 0.8)';
  const greenColor = 'rgba(16, 185, 129, 0.8)';
  
  if (darkMode) {
    // In dark mode, use brighter lime color
    return limeColor;
  } else {
    // In light mode, alternate between colors
    return index % 2 === 0 ? limeColor : greenColor;
  }
}

const PulsatingGradient = ({ darkMode }) => {
  return (
    <div
      className={`absolute inset-0 ${
        darkMode
          ? 'bg-lime-500 opacity-30'
          : 'bg-gradient-to-r from-lime-500 to-green-500'
      } shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl`}
      style={{
        animation: darkMode ? 'none' : 'pulse 5s ease-in-out infinite'
      }}
    />
  );
};

export { FloatingRingsBackground as RotatingRingsBackground, PulsatingGradient };
