import React, { useState, useEffect } from 'react';

const AnimatedErrorMessage = ({ message, onClose }) => {
  const [animationState, setAnimationState] = useState('enter');
  
  useEffect(() => {
    // Start talking animation after entering
    const talkingTimer = setTimeout(() => {
      setAnimationState('talking');
    }, 600);
    
    // Auto close after 4 seconds
    const closeTimer = setTimeout(() => {
      setAnimationState('exit');
      
      // Remove component after exit animation completes
      setTimeout(() => {
        if (onClose) onClose();
      }, 500);
    }, 4000);
    
    return () => {
      clearTimeout(talkingTimer);
      clearTimeout(closeTimer);
    };
  }, [onClose]);
  
  // Generate personalized messages based on the error
  const getErrorTitle = () => {
    if (message.includes("name")) {
      return "Hey there!";
    } else if (message.includes("email")) {
      return "Oops!";
    } else if (message.includes("phone")) {
      return "Just a sec!";
    } else if (message.includes("CV")) {
      return "Almost there!";
    } else {
      return "Hmm...";
    }
  };
  
  // Get animated character expression based on error type
  const getCharacterExpression = () => {
    if (message.includes("name")) {
      return "questioning";
    } else if (message.includes("email")) {
      return "confused";
    } else if (message.includes("phone")) {
      return "thinking";
    } else if (message.includes("CV")) {
      return "pointing";
    } else {
      return "default";
    }
  };
  
  const expression = getCharacterExpression();
  
  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      {/* Full screen background overlay with blur */}
      <div 
        className="absolute inset-0 transition-opacity duration-300" 
        style={{ 
          opacity: animationState === 'exit' ? 0 : 0.5,
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          backdropFilter: 'blur(4px)'
        }}
        onClick={() => setAnimationState('exit')}
      />
      
      {/* Message container with avatar */}
      <div 
        className={`bg-white rounded-lg p-4 sm:p-6 shadow-xl max-w-sm w-full mx-4 z-10 transition-all duration-500 transform flex relative
          ${animationState === 'enter' ? 'translate-y-0 scale-100' : ''} 
          ${animationState === 'exit' ? 'translate-y-16 scale-95 opacity-0' : ''}
          ${animationState === 'talking' ? 'scale-100' : 'scale-95'}
        `}
        style={{
          animation: animationState === 'enter' ? 'bounceIn 0.6s ease-in-out' : ''
        }}
      >
        {/* Close button */}
        <button 
          onClick={() => setAnimationState('exit')}
          className="absolute top-2 right-2 text-gray-400 hover:text-gray-700 z-10"
          aria-label="Close"
        >
          <svg className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
          </svg>
        </button>
        
        {/* Character and speech bubble layout */}
        <div className="flex w-full items-start">
          {/* Avatar character */}
          <div 
            className={`w-1/3 flex items-center justify-center relative
              ${animationState === 'talking' ? 'avatar-talking' : ''}
            `}
          >
            <div 
              className="relative w-24 h-24" 
              style={{
                animation: animationState === 'talking' ? 'headShake 1s ease-in-out infinite' : ''
              }}
            >
              {/* Avatar Head */}
              <div className="absolute inset-0">
                <svg viewBox="0 0 100 100" className="w-full h-full">
                  {/* Head shape */}
                  <circle cx="50" cy="50" r="40" fill="#FFD7B5" />
                  
                  {/* Hair */}
                  <path d="M20 40C20 20 40 10 50 10C60 10 80 20 80 40" fill="#6B5B95" />
                  <path d="M15 50C15 30 20 25 25 20" stroke="#6B5B95" strokeWidth="4" />
                  <path d="M85 50C85 30 80 25 75 20" stroke="#6B5B95" strokeWidth="4" />
                  
                  {/* Eyes with blinking animation */}
                  <g className="eyes">
                    <circle className="eye left-eye" cx="35" cy="45" r="5" fill="white" stroke="#333" strokeWidth="1" />
                    <circle className="eye-pupil" cx="35" cy="45" r="2" fill="#333">
                      <animate 
                        attributeName="cy" 
                        values="45;44;45;46;45" 
                        dur="2s" 
                        repeatCount="indefinite" 
                      />
                    </circle>
                    
                    <circle className="eye right-eye" cx="65" cy="45" r="5" fill="white" stroke="#333" strokeWidth="1" />
                    <circle className="eye-pupil" cx="65" cy="45" r="2" fill="#333">
                      <animate 
                        attributeName="cy" 
                        values="45;44;45;46;45" 
                        dur="2s" 
                        repeatCount="indefinite" 
                      />
                    </circle>
                    
                    {/* Blinking animation */}
                    <animate 
                      xlinkHref=".eye"
                      attributeName="ry" 
                      values="5;0.5;5" 
                      dur="3s"
                      repeatCount="indefinite"
                    />
                  </g>
                  
                  {/* Eyebrows - expression based */}
                  {expression === "questioning" && (
                    <g>
                      <path d="M30 35C30 35 35 32 40 35" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                      <path d="M60 32C60 32 65 35 70 32" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                    </g>
                  )}
                  
                  {expression === "confused" && (
                    <g>
                      <path d="M30 35C30 35 35 32 40 35" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                      <path d="M60 35C60 35 65 32 70 35" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                    </g>
                  )}
                  
                  {expression === "thinking" && (
                    <g>
                      <path d="M30 38C30 38 35 32 40 38" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                      <path d="M60 38C60 38 65 32 70 38" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                    </g>
                  )}
                  
                  {expression === "pointing" && (
                    <g>
                      <path d="M30 35C30 35 35 32 40 35" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                      <path d="M60 35C60 35 65 32 70 35" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                    </g>
                  )}
                  
                  {expression === "default" && (
                    <g>
                      <path d="M30 38C30 38 35 34 40 38" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                      <path d="M60 38C60 38 65 34 70 38" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                    </g>
                  )}
                  
                  {/* Mouth - talking animation with different expressions */}
                  <g className="mouth">
                    {expression === "questioning" && (
                      <path d="M40 65C45 62 55 62 60 65" stroke="#333" strokeWidth="2" fill="none" strokeLinecap="round">
                        <animate 
                          attributeName="d" 
                          values="M40 65C45 62 55 62 60 65;M40 68C45 65 55 65 60 68;M40 65C45 62 55 62 60 65" 
                          dur="0.6s"
                          repeatCount="indefinite"
                        />
                      </path>
                    )}
                    
                    {expression === "confused" && (
                      <path d="M40 68C45 65 55 65 60 68" stroke="#333" strokeWidth="2" fill="none" strokeLinecap="round">
                        <animate 
                          attributeName="d" 
                          values="M40 68C45 65 55 65 60 68;M40 65C45 62 55 62 60 65;M40 68C45 65 55 65 60 68" 
                          dur="0.6s"
                          repeatCount="indefinite"
                        />
                      </path>
                    )}
                    
                    {expression === "thinking" && (
                      <path d="M40 67C45 67 55 67 60 67" stroke="#333" strokeWidth="2" fill="none" strokeLinecap="round">
                        <animate 
                          attributeName="d" 
                          values="M40 67C45 67 55 67 60 67;M40 65C45 63 55 63 60 65;M40 67C45 67 55 67 60 67" 
                          dur="0.6s"
                          repeatCount="indefinite"
                        />
                      </path>
                    )}
                    
                    {expression === "pointing" && (
                      <path d="M45 65C50 62 55 62 60 65" stroke="#333" strokeWidth="2" fill="none" strokeLinecap="round">
                        <animate 
                          attributeName="d" 
                          values="M45 65C50 62 55 62 60 65;M45 68C50 65 55 65 60 68;M45 65C50 62 55 62 60 65" 
                          dur="0.6s"
                          repeatCount="indefinite"
                        />
                      </path>
                    )}
                    
                    {expression === "default" && (
                      <path d="M40 65C45 63 55 63 60 65" stroke="#333" strokeWidth="2" fill="none" strokeLinecap="round">
                        <animate 
                          attributeName="d" 
                          values="M40 65C45 63 55 63 60 65;M40 67C45 65 55 65 60 67;M40 65C45 63 55 63 60 65" 
                          dur="0.6s"
                          repeatCount="indefinite"
                        />
                      </path>
                    )}
                  </g>
                  
                  {/* Cheeks for friendlier appearance */}
                  <circle cx="30" cy="55" r="5" fill="#FFCBD1" fillOpacity="0.4" />
                  <circle cx="70" cy="55" r="5" fill="#FFCBD1" fillOpacity="0.4" />
                </svg>
              </div>
              
              {/* Body/Clothing - Optional, shown below head */}
              <div className="absolute -bottom-8 left-0 right-0">
                <svg viewBox="0 0 100 60" className="w-full">
                  <path d="M30 0C30 0 40 10 50 10C60 10 70 0 70 0L80 50H20L30 0Z" fill="#4F46E5" />
                  <path d="M45 10L50 20L55 10" fill="#FFFFFF" />
                </svg>
              </div>
            </div>
            
            {/* Pointing hand for "pointing" expression */}
            {expression === "pointing" && (
              <div className="absolute bottom-0 left-2 transform rotate-45">
                <svg width="30" height="30" viewBox="0 0 50 50">
                  <path d="M10 30C5 25 5 15 10 10C15 5 25 15 25 15L30 5C30 5 35 10 30 15L35 10C35 10 40 15 35 20L40 15C40 15 45 20 40 25L45 20C45 20 50 25 45 30C40 35 25 40 20 40C15 40 15 35 10 30Z" fill="#FFD7B5" />
                </svg>
              </div>
            )}
            
            {/* Thinking bubble for "thinking" expression */}
            {expression === "thinking" && (
              <div className="absolute top-0 right-0">
                <svg width="20" height="20" viewBox="0 0 100 100">
                  <circle cx="90" cy="20" r="10" fill="#E5E7EB" />
                  <circle cx="70" cy="30" r="7" fill="#E5E7EB" />
                  <circle cx="55" cy="40" r="5" fill="#E5E7EB" />
                </svg>
              </div>
            )}
          </div>
          
          {/* Message bubble */}
          <div className="w-2/3 flex flex-col pl-2">
            <div
              className="px-3 py-2 bg-gray-100 rounded-lg rounded-tl-none mt-2 
                         relative shadow-md transition-all"
              style={{
                animation: animationState === 'talking' ? 'pulse 2s infinite' : ''
              }}
            >
              {/* Speech bubble tail */}
              <div 
                className="absolute -left-2 top-3 w-0 h-0" 
                style={{
                  borderTop: '8px solid transparent',
                  borderBottom: '8px solid transparent',
                  borderRight: '8px solid #f3f4f6',
                }}
              ></div>
              
              <h3 className="font-bold text-gray-800">{getErrorTitle()}</h3>
              <p className="text-gray-700 text-sm mt-1">{message}</p>
            </div>
            
            {/* Typing animation dots */}
            {animationState === 'talking' && (
              <div className="flex space-x-1 mt-2 ml-2">
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }}></div>
              </div>
            )}
          </div>
        </div>
      </div>
      
      {/* Add CSS animations */}
      <style jsx>{`
        @keyframes bounceIn {
          0% { transform: scale(0.3); opacity: 0; }
          50% { transform: scale(1.05); }
          70% { transform: scale(0.9); }
          100% { transform: scale(1); opacity: 1; }
        }
        
        @keyframes pulse {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.03); }
        }
        
        @keyframes headShake {
          0% { transform: rotate(0deg); }
          25% { transform: rotate(-5deg); }
          50% { transform: rotate(0deg); }
          75% { transform: rotate(5deg); }
          100% { transform: rotate(0deg); }
        }
        
        .avatar-talking .mouth {
          animation: talking 0.6s infinite;
        }
        
        @keyframes talking {
          0%, 100% { transform: scaleY(1); }
          50% { transform: scaleY(1.2); }
        }
      `}</style>
    </div>
  );
};

export default AnimatedErrorMessage;
