import React, { useState, useEffect } from 'react';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import AnimatedErrorMessage from './AnimatedErrorMessage';

// Common country codes with flags
const countryCodes = [
  { code: '+1', flag: 'ðŸ‡ºðŸ‡¸', name: 'United States' },
  { code: '+44', flag: 'ðŸ‡¬ðŸ‡§', name: 'United Kingdom' },
  { code: '+91', flag: 'ðŸ‡®ðŸ‡³', name: 'India' },
  { code: '+61', flag: 'ðŸ‡¦ðŸ‡º', name: 'Australia' },
  { code: '+49', flag: 'ðŸ‡©ðŸ‡ª', name: 'Germany' },
  { code: '+33', flag: 'ðŸ‡«ðŸ‡·', name: 'France' },
  { code: '+81', flag: 'ðŸ‡¯ðŸ‡µ', name: 'Japan' },
  { code: '+86', flag: 'ðŸ‡¨ðŸ‡³', name: 'China' },
  { code: '+7', flag: 'ðŸ‡·ðŸ‡º', name: 'Russia' },
  { code: '+55', flag: 'ðŸ‡§ðŸ‡·', name: 'Brazil' },
  { code: '+52', flag: 'ðŸ‡²ðŸ‡½', name: 'Mexico' },
  { code: '+34', flag: 'ðŸ‡ªðŸ‡¸', name: 'Spain' },
  { code: '+39', flag: 'ðŸ‡®ðŸ‡¹', name: 'Italy' },
  { code: '+82', flag: 'ðŸ‡°ðŸ‡·', name: 'South Korea' },
  { code: '+65', flag: 'ðŸ‡¸ðŸ‡¬', name: 'Singapore' },
  { code: '+971', flag: 'ðŸ‡¦ðŸ‡ª', name: 'UAE' },
  { code: '+966', flag: 'ðŸ‡¸ðŸ‡¦', name: 'Saudi Arabia' },
  { code: '+27', flag: 'ðŸ‡¿ðŸ‡¦', name: 'South Africa' },
  { code: '+234', flag: 'ðŸ‡³ðŸ‡¬', name: 'Nigeria' },
  { code: '+94', flag: 'ðŸ‡±ðŸ‡°', name: 'Sri Lanka' },
];

const ApplicationForm = ({ darkMode, onSubmitSuccess }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phoneCountryCode: '+94', // Default to Sri Lanka
    phoneNumber: '',
    cv: null
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [fileName, setFileName] = useState('');
  const [countryCodeDropdownOpen, setCountryCodeDropdownOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [isFormValid, setIsFormValid] = useState(false);

  // Find the default country code object
  const selectedCountry = countryCodes.find(country => country.code === formData.phoneCountryCode);

  // Filter countries based on search term
  const filteredCountries = countryCodes.filter(country => 
    country.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
    country.code.includes(searchTerm)
  );

  // State for the enhanced error animation
  const [showErrorAnimation, setShowErrorAnimation] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  // Check form validity whenever form data changes
  useEffect(() => {
    const { name, email, phoneNumber, cv } = formData;
    const valid = 
      name.trim() !== '' && 
      email.trim() !== '' && 
      /\S+@\S+\.\S+/.test(email) && 
      phoneNumber.trim() !== '' && 
      /^[0-9\s\-()]{8,15}$/.test(phoneNumber) &&
      cv !== null;
    
    setIsFormValid(valid);
  }, [formData]);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = () => {
      setCountryCodeDropdownOpen(false);
    };

    if (countryCodeDropdownOpen) {
      document.addEventListener('click', handleClickOutside);
    }

    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [countryCodeDropdownOpen]);

  // Handle form field changes with animation for validation errors
  const handleChange = (e) => {
    const { name, value } = e.target;
    
    setFormData({
      ...formData,
      [name]: value
    });
    
    // Clear error for this field if it exists
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: null
      });
    }
    
    // Animate field if invalid
    const fieldElement = document.getElementById(name);
    if (fieldElement) {
      // Remove any existing animation classes
      fieldElement.classList.remove('shake');
      
      // Check for basic validation errors
      let hasError = false;
      
      if (name === 'email' && value.trim() !== '' && !/\S+@\S+\.\S+/.test(value)) {
        hasError = true;
      } else if (name === 'phoneNumber' && value.trim() !== '' && !/^[0-9\s\-()]{8,15}$/.test(value)) {
        hasError = true;
      }
      
      // Add animation if there's an error
      if (hasError) {
        // Trigger reflow to restart animation
        void fieldElement.offsetWidth;
        fieldElement.classList.add('shake');
      }
    }
  };

  // Handle country code selection
  const handleCountryCodeSelect = (code) => {
    setFormData({
      ...formData,
      phoneCountryCode: code
    });
    setCountryCodeDropdownOpen(false);
    setSearchTerm('');
  };

  // Toggle dropdown and prevent event propagation
  const toggleDropdown = (e) => {
    e.stopPropagation();
    setCountryCodeDropdownOpen(!countryCodeDropdownOpen);
  };
  
  // Handle search input and prevent event propagation
  const handleSearchChange = (e) => {
    e.stopPropagation();
    setSearchTerm(e.target.value);
  };

  // Handle file upload with animation
  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check file type
      const fileType = file.type;
      if (fileType !== 'application/pdf' && 
          fileType !== 'application/vnd.openxmlformats-officedocument.wordprocessingml.document') {
        setErrors({
          ...errors,
          cv: 'Only PDF and DOCX files are allowed'
        });
        
        // Show error animation
        setErrorMessage("Oops! Only PDF or DOCX files allowed. Can you try a different file?");
        setShowErrorAnimation(true);
        return;
      }
      
      // Check file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setErrors({
          ...errors,
          cv: 'File size should not exceed 5MB'
        });
        
        // Show error animation
        setErrorMessage("Whoa! That file is a bit too large. Can you keep it under 5MB?");
        setShowErrorAnimation(true);
        return;
      }

      setFormData({
        ...formData,
        cv: file
      });
      setFileName(file.name);
      
      // Clear error for CV if it exists
      if (errors.cv) {
        setErrors({
          ...errors,
          cv: null
        });
      }
    }
  };

  // Get personalized error message
  const getPersonalizedErrorMessage = () => {
    if (!formData.name.trim()) {
      return "Hey there! I don't know what to call you. Could you share your name?";
    } else if (!formData.email.trim() || !/\S+@\S+\.\S+/.test(formData.email)) {
      return "I'd love to stay in touch! Please provide a valid email address.";
    } else if (!formData.phoneNumber.trim() || !/^[0-9\s\-()]{8,15}$/.test(formData.phoneNumber)) {
      return "Just a sec! We'll need your phone number to reach you. Make sure it's valid!";
    } else if (!formData.cv) {
      return "Almost there! Don't forget to attach your CV so we can see your awesome skills!";
    } else {
      return "Please fill in all required fields correctly before submitting.";
    }
  };

  // Validate form
  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.phoneNumber.trim()) {
      newErrors.phoneNumber = 'Phone number is required';
    } else if (!/^[0-9\s\-()]{8,15}$/.test(formData.phoneNumber)) {
      newErrors.phoneNumber = 'Phone number is invalid';
    }

    if (!formData.cv) {
      newErrors.cv = 'CV document is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission with enhanced animations
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (validateForm()) {
      setLoading(true);
      
      try {
        // Create form data for file upload
        const data = new FormData();
        data.append('name', formData.name);
        data.append('email', formData.email);
        data.append('phone', `${formData.phoneCountryCode}${formData.phoneNumber}`);
        data.append('cv', formData.cv);

        // Submit form to API - In a real application, you'd have the actual endpoint
        // For now, we'll simulate a successful submission after a delay
        await new Promise(resolve => setTimeout(resolve, 1500));
        
        // Simulate successful response
        // const response = await axios.post('http://localhost:5000/api/applications', data, {
        //   headers: {
        //     'Content-Type': 'multipart/form-data'
        //   }
        // });

        // Trigger success animation
        if (onSubmitSuccess) {
          onSubmitSuccess();
        }
        
        // Reset form
        setFormData({
          name: '',
          email: '',
          phoneCountryCode: '+94',
          phoneNumber: '',
          cv: null
        });
        setFileName('');
        
      } catch (error) {
        console.error('Error submitting application:', error);
        
        // Handle error
        if (error.response && error.response.data && error.response.data.message) {
          toast.error(error.response.data.message);
        } else {
          toast.error('Failed to submit application. Please try again.');
        }
      } finally {
        setLoading(false);
      }
    } else {
      // Show enhanced error animation with personalized message
      setErrorMessage(getPersonalizedErrorMessage());
      setShowErrorAnimation(true);
      
      // Animate invalid fields
      Object.keys(errors).forEach(fieldName => {
        const fieldElement = document.getElementById(fieldName);
        if (fieldElement) {
          // Remove existing animation
          fieldElement.classList.remove('shake');
          
          // Trigger reflow to restart animation
          void fieldElement.offsetWidth;
          
          // Add animation
          fieldElement.classList.add('shake');
        }
      });
    }
  };

  return (
    <div className="max-w-md mx-auto text-gray-800 transition-colors">
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Name Field with animation */}
        <div className="animate-bounce-in" style={{ animationDelay: '0.1s' }}>
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="name">
            Full Name
          </label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className={`w-full px-3 py-2 border rounded-md transition-all duration-300 ${
              errors.name 
                ? 'border-red-500' 
                : 'border-gray-300 focus:border-lime-500 focus:ring focus:ring-lime-500/50'
            }`}
            placeholder="Cusherah Kugan"
          />
          {errors.name && <p className="text-red-500 text-xs mt-1">{errors.name}</p>}
        </div>

        {/* Email Field with animation */}
        <div className="animate-bounce-in" style={{ animationDelay: '0.2s' }}>
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="email">
            Email Address
          </label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className={`w-full px-3 py-2 border rounded-md transition-all duration-300 ${
              errors.email 
                ? 'border-red-500' 
                : 'border-gray-300 focus:border-lime-500 focus:ring focus:ring-lime-500/50'
            }`}
            placeholder="cusherahkugan@gmail.com"
          />
          {errors.email && <p className="text-red-500 text-xs mt-1">{errors.email}</p>}
        </div>

        {/* Phone Field with Country Code Dropdown - with animations */}
        <div className="animate-bounce-in" style={{ animationDelay: '0.3s' }}>
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="phoneNumber">
            Phone Number
          </label>
          <div className="flex flex-col sm:flex-row relative phone-field-container">
            {/* Country code dropdown with animations */}
            <div className="relative mb-2 sm:mb-0">
              <button
                type="button"
                onClick={toggleDropdown}
                className={`flex items-center justify-between w-full sm:w-[110px] h-10 px-3 py-2 border ${
                  errors.phoneNumber ? 'border-red-500' : 'border-gray-300'
                } country-code-selector bg-white text-gray-700 transition-all duration-300 focus:outline-none focus:border-lime-500 focus:ring focus:ring-lime-500/50`}
              >
                <span className="flex items-center">
                  {selectedCountry ? (
                    <>
                      <span className="mr-1">{selectedCountry.flag}</span>
                      <span>{selectedCountry.code}</span>
                    </>
                  ) : "Select"}
                </span>
                <svg 
                  xmlns="http://www.w3.org/2000/svg" 
                  className={`h-4 w-4 ml-1 transition-transform duration-300 ${countryCodeDropdownOpen ? 'rotate-180' : ''}`} 
                  fill="none" 
                  viewBox="0 0 24 24" 
                  stroke="currentColor"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </button>

              {/* Dropdown with animation */}
              {countryCodeDropdownOpen && (
                <div className="absolute z-50 mt-1 w-[250px] p-2 bg-white border border-gray-200 rounded-md shadow-lg max-h-[300px] overflow-y-auto animate-bounce-in">
                  <div className="sticky top-0 bg-white pb-2">
                    <input
                      type="text"
                      placeholder="Search country..."
                      value={searchTerm}
                      onChange={handleSearchChange}
                      onClick={(e) => e.stopPropagation()}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:border-lime-500 transition-all duration-300"
                    />
                  </div>
                  
                  {filteredCountries.length === 0 ? (
                    <div className="p-2 text-center text-gray-500">No country found</div>
                  ) : (
                    <div>
                      {filteredCountries.map((country) => (
                        <div
                          key={country.code}
                          className={`flex items-center p-2 cursor-pointer hover:bg-gray-100 rounded-md transition-all duration-200 ${
                            formData.phoneCountryCode === country.code ? 'bg-lime-100' : ''
                          }`}
                          onClick={(e) => {
                            e.stopPropagation();
                            handleCountryCodeSelect(country.code);
                          }}
                        >
                          <span className="mr-2">{country.flag}</span>
                          <span className="flex-1">{country.name}</span>
                          <span className="text-gray-500">{country.code}</span>
                          {formData.phoneCountryCode === country.code && (
                            <svg className="w-4 h-4 ml-2 text-lime-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                            </svg>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              )}
            </div>

            {/* Phone number input */}
            <input
              type="tel"
              id="phoneNumber"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleChange}
              className={`flex-1 px-3 py-2 border phone-input transition-all duration-300 ${
                errors.phoneNumber 
                  ? 'border-red-500' 
                  : 'border-gray-300 focus:border-lime-500 focus:ring focus:ring-lime-500/50'
              }`}
              placeholder="766741405"
            />
          </div>
          {errors.phoneNumber && <p className="text-red-500 text-xs mt-1">{errors.phoneNumber}</p>}
        </div>

        {/* CV Upload Field with animation */}
        <div className="mt-4 animate-bounce-in" style={{ animationDelay: '0.4s' }}>
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="cv">
            CV/Resume
          </label>
          <div className={`relative border-dashed border-2 rounded-lg p-4 sm:p-6 mt-1 transition-all duration-300 ${
            errors.cv 
              ? 'border-red-500 bg-red-50' 
              : fileName 
                ? 'border-lime-500 bg-lime-50' 
                : 'border-gray-300 hover:border-gray-400 hover:bg-gray-50'
          }`}>
            <input
              type="file"
              id="cv"
              name="cv"
              onChange={handleFileUpload}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
              accept=".pdf,.docx,application/pdf,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
            />
            <div className="text-center">
              {fileName ? (
                <div className="flex items-center justify-center animate-bounce-in">
                  <svg 
                    className="h-8 w-8 text-lime-500 mr-2" 
                    fill="none" 
                    viewBox="0 0 24 24" 
                    stroke="currentColor"
                  >
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                  </svg>
                  <span className="text-sm font-medium text-gray-900">{fileName}</span>
                </div>
              ) : (
                <>
                  <svg 
                    className={`mx-auto h-10 w-10 ${errors.cv ? 'text-red-500' : 'text-gray-500'} transition-all duration-300`}
                    stroke="currentColor" 
                    fill="none" 
                    viewBox="0 0 48 48"
                  >
                    <path d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4h-4m-12 4h-8" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
                  </svg>
                  <p className={`mt-1 text-sm ${errors.cv ? 'text-red-600' : 'text-gray-600'} transition-all duration-300`}>
                    Drag and drop or click to upload your CV (PDF or DOCX)
                  </p>
                  <p className="mt-1 text-xs text-gray-500">
                    Max file size: 5MB
                  </p>
                </>
              )}
            </div>
          </div>
          {errors.cv && <p className="text-red-500 text-xs mt-1">{errors.cv}</p>}
        </div>

        {/* Submit Button - Always enabled */}
        <div className="flex items-center justify-center mt-6">
          <button
            type="submit"
            disabled={loading}
            className={`bg-lime-500 hover:bg-lime-600 text-white font-bold py-2 px-6 rounded-md focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-lime-500 transition-colors ${
              loading ? 'opacity-50 cursor-not-allowed' : ''
            }`}
          >
            {loading ? (
              <div className="flex items-center">
                <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-current" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Submitting...
              </div>
            ) : 'Submit Application'}
          </button>
        </div>
        
        {/* Error Animation with Full Character */}
        {showErrorAnimation && (
          <div className="fixed inset-0 flex items-center justify-center z-50">
            <div className="absolute inset-0 bg-black bg-opacity-50" onClick={() => setShowErrorAnimation(false)}></div>
            <div className="bg-white rounded-lg p-4 sm:p-6 shadow-xl max-w-sm w-full mx-4 z-10 transform transition-all animate-bounce-in">
              <div className="flex flex-col items-center mb-4">
                {/* Close button at top right */}
                <button 
                  onClick={() => setShowErrorAnimation(false)}
                  className="absolute top-2 right-2 text-gray-400 hover:text-gray-700"
                  aria-label="Close"
                >
                  <svg className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
                  </svg>
                </button>
                
                {/* Full character SVG with animations */}
                <div className="animate-head-shake">
                  <svg width="120" height="160" viewBox="0 0 120 160" fill="none" xmlns="http://www.w3.org/2000/svg">
                    {/* Head */}
                    <circle cx="60" cy="50" r="30" fill="#FFD7B5" />
                    
                    {/* Hair */}
                    <path d="M35 30C35 30 40 15 60 15C80 15 85 30 85 30" stroke="#333" strokeWidth="4" fill="#555" />
                    <path d="M30 50C30 50 25 30 35 25C45 20 40 35 40 35" stroke="#333" strokeWidth="2" fill="#555" />
                    <path d="M90 50C90 50 95 30 85 25C75 20 80 35 80 35" stroke="#333" strokeWidth="2" fill="#555" />
                    
                    {/* Eyes */}
                    <circle cx="48" cy="45" r="3" fill="#333" />
                    <circle cx="72" cy="45" r="3" fill="#333" />
                    
                    {/* Eyebrows raised in concern */}
                    <path d="M45 38C45 38 48 36 51 38" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                    <path d="M75 38C75 38 72 36 69 38" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                    
                    {/* Mouth - concerned expression */}
                    <path d="M53 65C57 62 63 62 67 65" stroke="#333" strokeWidth="2" strokeLinecap="round" />
                    
                    {/* Body - business casual */}
                    <rect x="45" y="80" width="30" height="45" fill="#3B82F6" rx="5" /> {/* Blue shirt */}
                    <rect x="40" y="125" width="40" height="30" fill="#374151" rx="2" /> {/* Dark pants */}
                    
                    {/* Arms */}
                    <rect x="30" y="85" width="15" height="40" fill="#FFD7B5" rx="7" transform="rotate(15 30 85)" /> {/* Left arm */}
                    <rect x="82" y="83" width="15" height="40" fill="#FFD7B5" rx="7" transform="rotate(-15 82 83)" /> {/* Right arm */}
                    
                    {/* Hands */}
                    <circle cx="25" cy="125" r="6" fill="#FFD7B5" />
                    <circle cx="95" cy="125" r="6" fill="#FFD7B5" />
                    
                    {/* Tie */}
                    <path d="M60 80L65 90L60 110L55 90L60 80Z" fill="#EF4444" />
                    
                    {/* Clipboard */}
                    <rect x="20" y="125" width="20" height="25" fill="#D1D5DB" rx="2" />
                    <rect x="22" y="130" width="16" height="2" fill="#6B7280" />
                    <rect x="22" y="135" width="16" height="2" fill="#6B7280" />
                    <rect x="22" y="140" width="16" height="2" fill="#6B7280" />
                    
                    {/* Question mark - animated separately */}
                    <text x="90" y="115" fontSize="28" fill="#EF4444" fontWeight="bold">?</text>
                  </svg>
                </div>
                
                {/* Message */}
                <div className="text-center mt-4">
                  <h3 className="text-lg font-bold text-red-600">Oops!</h3>
                  <p className="text-gray-700">{errorMessage}</p>
                </div>
              </div>
            </div>
          </div>
        )}
      </form>

      <ToastContainer 
        position="bottom-right"
        theme="light"
      />
    </div>
  );
};

export default ApplicationForm;
