import React, { useState, useEffect } from 'react';

// Rotating rings background animation
const RotatingRingsBackground = ({ darkMode }) => {
  const [rings, setRings] = useState([]);
  
  useEffect(() => {
    // Generate random ring elements
    const generateRings = () => {
      const ringsCount = 6;
      const newRings = [];
      
      for (let i = 0; i < ringsCount; i++) {
        // Calculate random properties for each ring
        const size = 150 + Math.random() * 300; // Size between 150px and 450px
        const xPos = Math.random() * 100; // Position between 0% and 100%
        const yPos = Math.random() * 100;
        const rotation = Math.random() * 360; // Random initial rotation
        const duration = 20 + Math.random() * 40; // Animation duration between 20s and 60s
        const delay = Math.random() * -20; // Random delay
        const direction = Math.random() > 0.5 ? 1 : -1; // Clockwise or counter-clockwise
        const thickness = 3 + Math.random() * 8; // Ring thickness between 3px and 11px
        
        newRings.push({
          id: i,
          size,
          x: xPos,
          y: yPos,
          rotation,
          duration,
          delay,
          direction,
          thickness
        });
      }
      
      setRings(newRings);
    };
    
    generateRings();
  }, []);

  return (
    <div className="absolute inset-0 overflow-hidden z-0" style={{ pointerEvents: 'none' }}>
      {rings.map((ring) => (
        <div 
          key={ring.id}
          className="absolute rounded-full border-2"
          style={{
            width: `${ring.size}px`,
            height: `${ring.size}px`,
            left: `calc(${ring.x}% - ${ring.size / 2}px)`,
            top: `calc(${ring.y}% - ${ring.size / 2}px)`,
            borderWidth: `${ring.thickness}px`,
            borderColor: darkMode ? 'rgba(132, 204, 22, 0.4)' : 'rgba(16, 185, 129, 0.4)',
            borderStyle: 'solid',
            opacity: 0.2,
            animation: `${ring.duration}s linear ${ring.delay}s infinite`,
            animationName: ring.direction > 0 ? 'spin' : 'spin-reverse',
            transform: `rotate(${ring.rotation}deg)`
          }}
        />
      ))}
    </div>
  );
};

const PulsatingGradient = ({ darkMode }) => {
  return (
    <div
      className={`absolute inset-0 ${
        darkMode
          ? 'bg-lime-500 opacity-30'
          : 'bg-gradient-to-r from-lime-400 to-green-500'
      } shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl`}
      style={{
        animation: darkMode ? 'none' : 'pulse 5s ease-in-out infinite'
      }}
    />
  );
};

// Animated error message component
const AnimatedErrorMessage = ({ message, onClose }) => {
  return (
    <div className="fixed inset-0 flex items-center justify-center z-50" aria-modal="true" role="dialog">
      {/* Full-screen backdrop */}
      <div 
        className="fixed inset-0 bg-black bg-opacity-50 transition-opacity duration-300" 
        onClick={onClose}
      />
      
      <div className="bg-white rounded-lg p-4 sm:p-6 shadow-xl max-w-sm w-full mx-4 z-50 relative transition-all duration-500 transform flex">
        {/* Close button */}
        <button 
          onClick={onClose}
          className="absolute top-2 right-2 text-gray-400 hover:text-gray-700 z-50"
          aria-label="Close"
        >
          <svg width="20" height="20" fill="currentColor" viewBox="0 0 20 20">
            <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
          </svg>
        </button>
        
        {/* Character and speech bubble layout */}
        <div className="flex w-full">
          {/* Animated character */}
          <div className="w-1/3 flex items-center justify-center">
            <svg width="80" height="120" viewBox="0 0 120 160" fill="none">
              {/* Head */}
              <circle cx="60" cy="50" r="30" fill="#FFD7B5" />
              
              {/* Hair */}
              <path d="M35 30C35 30 40 15 60 15C80 15 85 30 85 30" stroke="#333" strokeWidth="4" fill="#555" />
              <path d="M30 50C30 50 25 30 35 25C45 20 40 35 40 35" stroke="#333" strokeWidth="2" fill="#555" />
              <path d="M90 50C90 50 95 30 85 25C75 20 80 35 80 35" stroke="#333" strokeWidth="2" fill="#555" />
              
              {/* Eyes */}
              <circle cx="48" cy="45" r="3" fill="#333" />
              <circle cx="72" cy="45" r="3" fill="#333" />
              
              {/* Eyebrows */}
              <path d="M45 38C45 38 48 36 51 38" stroke="#333" strokeWidth="2" strokeLinecap="round" />
              <path d="M69 38C69 38 72 36 75 38" stroke="#333" strokeWidth="2" strokeLinecap="round" />
              
              {/* Mouth */}
              <path d="M53 60C57 65 63 65 67 60" stroke="#333" strokeWidth="2" strokeLinecap="round" />
              
              {/* Body */}
              <rect x="45" y="80" width="30" height="45" fill="#3B82F6" rx="5" />
              <rect x="40" y="125" width="40" height="30" fill="#374151" rx="2" />
              
              {/* Arms */}
              <rect x="30" y="85" width="15" height="40" fill="#FFD7B5" rx="7" transform="rotate(-15 30 85)" />
              <rect x="82" y="83" width="15" height="40" fill="#FFD7B5" rx="7" transform="rotate(-15 82 83)" />
              
              {/* Hands */}
              <circle cx="25" cy="125" r="6" fill="#FFD7B5" />
              <circle cx="95" cy="125" r="6" fill="#FFD7B5" />
              
              {/* Tie */}
              <path d="M60 80L65 90L60 110L55 90L60 80Z" fill="#EF4444" />
            </svg>
          </div>
          
          {/* Message bubble */}
          <div className="w-2/3 flex flex-col pl-2">
            <div className="px-3 py-2 bg-gray-100 rounded-lg rounded-tl-none mt-2">
              <h3 className="font-bold text-gray-800">Attention Required</h3>
              <p className="text-gray-700 text-sm">{message}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const JobApplicationDemo = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [isMobile, setIsMobile] = useState(window.innerWidth < 640);
  const [phoneSection, setPhoneSection] = useState('collapsed');
  const [showError, setShowError] = useState(false);
  
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 640);
    };
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };
  
  const togglePhoneSection = () => {
    setPhoneSection(phoneSection === 'collapsed' ? 'expanded' : 'collapsed');
  };
  
  const showErrorMessage = () => {
    setErrorMessage("We'd love to know your name to personalize your application experience.");
    setShowError(true);
  };

  return (
    <div 
      className={`min-h-screen transition-colors duration-500 ease-in-out ${
        darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'
      } py-4 sm:py-8 flex flex-col justify-center overflow-hidden relative`}
      style={{ height: '600px' }}
    >
      {/* Rotating rings background */}
      <RotatingRingsBackground darkMode={darkMode} />
      
      {/* Dark mode toggle */}
      <div className="absolute top-4 right-4 flex items-center space-x-2 bg-white dark:bg-gray-800 p-2 rounded-full shadow-lg z-50">
        <button 
          onClick={toggleDarkMode}
          className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors focus:outline-none ${
            darkMode ? 'bg-lime-500' : 'bg-gray-300'
          }`}
        >
          <span 
            className={`inline-block h-4 w-4 transform rounded-full bg-white transition-all duration-300 ${
              darkMode ? 'translate-x-6' : 'translate-x-1'
            }`} 
          />
        </button>
        
        <div className="flex items-center justify-center w-6 h-6 text-gray-700 dark:text-yellow-300">
          {darkMode ? (
            <svg width="20" height="20" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
            </svg>
          ) : (
            <svg width="20" height="20" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
            </svg>
          )}
        </div>
      </div>
      
      <div className="relative py-3 px-4 sm:px-0 w-full max-w-xl mx-auto">
        {/* Improved background gradient */}
        <PulsatingGradient darkMode={darkMode} />
        
        {/* Form container */}
        <div 
          className="relative px-4 py-6 sm:px-8 md:p-10 bg-white shadow-xl sm:rounded-xl z-10"
          style={{
            backdropFilter: 'blur(12px)',
            WebkitBackdropFilter: 'blur(12px)'
          }}
        >
          <div className="max-w-md mx-auto">
            <div className="text-center mb-6">
              <h1 className="text-2xl sm:text-3xl font-extrabold text-gray-900 mb-4">
                Job Application
              </h1>
              <p className="text-gray-600 mb-6">
                Join our team by completing this application form below.
              </p>
            </div>
            
            {/* Form preview */}
            <div className="space-y-4">
              {/* Name Field */}
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="demo-name">
                  Full Name
                </label>
                <input
                  type="text"
                  id="demo-name"
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  placeholder="Your full name"
                />
              </div>

              {/* Email Field */}
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="demo-email">
                  Email Address
                </label>
                <input
                  type="email"
                  id="demo-email"
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  placeholder="example@domain.com"
                />
              </div>

              {/* Phone Field with Country Code Dropdown */}
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Phone Number
                </label>
                <div className={`flex ${isMobile ? 'flex-col space-y-2' : 'flex-row space-x-2'}`}>
                  {/* Country code dropdown */}
                  <div className="relative">
                    <button
                      type="button"
                      onClick={togglePhoneSection}
                      className={`flex items-center justify-between ${isMobile ? 'w-full' : 'w-32'} px-3 py-2 border border-gray-300 bg-white text-gray-700 rounded-md`}
                    >
                      <span className="flex items-center">
                        <span className="mr-1">🇱🇰</span>
                        <span>+94</span>
                      </span>
                      <svg width="16" height="16" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                      </svg>
                    </button>
                  </div>

                  {/* Phone number input */}
                  <input
                    type="tel"
                    className="flex-1 px-3 py-2 border border-gray-300 rounded-md"
                    placeholder="Enter your phone number"
                  />
                </div>
              </div>

              {/* CV Upload Field */}
              <div className="mt-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  CV/Resume
                </label>
                <div className="relative border-dashed border-2 rounded-lg p-4 sm:p-6 mt-1 border-gray-300 hover:border-gray-400 hover:bg-gray-50">
                  <div className="text-center">
                    <svg 
                      className="mx-auto h-10 w-10 text-gray-500"
                      stroke="currentColor" 
                      fill="none" 
                      viewBox="0 0 48 48"
                    >
                      <path d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4h-4m-12 4h-8" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
                    </svg>
                    <p className="mt-1 text-sm text-gray-600">
                      Click to upload your CV (PDF or DOCX)
                    </p>
                    <p className="mt-1 text-xs text-gray-500">
                      Max file size: 5MB
                    </p>
                  </div>
                </div>
              </div>

              {/* Submit Button */}
              <div className="flex items-center justify-center mt-6">
                <button
                  onClick={showErrorMessage}
                  className={`bg-lime-500 hover:bg-lime-600 text-white font-bold py-2 px-6 rounded-md transition-all transform duration-300 hover:scale-105 ${isMobile ? 'w-full' : ''}`}
                >
                  Submit Application
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Error message component */}
      {showError && (
        <AnimatedErrorMessage 
          message={errorMessage} 
          onClose={() => setShowError(false)}
        />
      )}
      
      {/* Add keyframes for animations */}
      <style jsx>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
        
        @keyframes spin-reverse {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(-360deg); }
        }
        
        @keyframes pulse {
          0%, 100% {
            opacity: 1;
            transform: scale(1);
          }
          50% {
            opacity: 0.85;
            transform: scale(1.05);
          }
        }
      `}</style>
    </div>
  );
};

export default JobApplicationDemo;
