import React, { useEffect, useState } from 'react';

// Component to create animated bubbles in the background
const AnimatedBackground = ({ darkMode }) => {
  const [bubbles, setBubbles] = useState([]);
  
  useEffect(() => {
    // Create random bubbles
    const createBubbles = () => {
      const newBubbles = [];
      const bubbleCount = window.innerWidth < 768 ? 10 : 20; // Fewer bubbles on mobile
      
      for (let i = 0; i < bubbleCount; i++) {
        newBubbles.push({
          id: i,
          size: Math.random() * 50 + 10, // 10-60px
          left: Math.random() * 100 + '%',
          delay: Math.random() * 5 + 's',
          duration: Math.random() * 10 + 15 + 's', // 15-25s
        });
      }
      
      setBubbles(newBubbles);
    };
    
    createBubbles();
    
    // Recreate bubbles when window is resized
    const handleResize = () => {
      createBubbles();
    };
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);
  
  return (
    <div className={`absolute inset-0 overflow-hidden pointer-events-none ${darkMode ? 'dark-mode-bubbles' : 'light-mode-bubbles'}`}>
      {bubbles.map((bubble) => (
        <div
          key={bubble.id}
          className="bubble"
          style={{
            width: bubble.size + 'px',
            height: bubble.size + 'px',
            left: bubble.left,
            bottom: '-100px',
            animationDelay: bubble.delay,
            animationDuration: bubble.duration
          }}
        />
      ))}
    </div>
  );
};

// Animated job-related icons that float across the background
const JobAnimationBackground = ({ darkMode }) => {
  return (
    <div className="job-animation-container" style={{ opacity: darkMode ? 0.15 : 0.08 }}>
      {/* Additional job-related animations are handled in CSS */}
    </div>
  );
};

// Pulsating gradient for the background skew element
const PulsatingGradient = ({ darkMode }) => {
  return (
    <div 
      className={`absolute inset-0 ${
        darkMode 
          ? 'bg-lime-500 opacity-30' 
          : 'bg-gradient-to-r from-lime-400 to-green-500'
      } shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl`}
      style={{
        animation: darkMode ? 'none' : 'pulse 5s ease-in-out infinite'
      }}
    />
  );
};

export { AnimatedBackground, JobAnimationBackground, PulsatingGradient };
