  const [fileName, setFileName] = useState('');
  const [countryCodeOpen, setCountryCodeOpen] = useState(false);
  const [isFormValid, setIsFormValid] = useState(false);

  // Find the default country code object
  const defaultCountry = countryCodes.find(country => country.code === formData.phoneCountryCode);

  // Check form validity whenever form data changes
  useEffect(() => {
    const { name, email, phoneNumber, cv } = formData;
    const valid = 
      name.trim() !== '' && 
      email.trim() !== '' && 
      /\S+@\S+\.\S+/.test(email) && 
      phoneNumber.trim() !== '' && 
      /^[0-9\s\-()]{8,15}$/.test(phoneNumber) &&
      cv !== null;
    
    setIsFormValid(valid);
  }, [formData]);

  // Handle form field changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    
    // Clear error for this field if it exists
    if (errors[e.target.name]) {
      setErrors({
        ...errors,
        [e.target.name]: null
      });
    }
  };

  // Handle country code selection
  const handleCountryCodeSelect = (code) => {
    setFormData({
      ...formData,
      phoneCountryCode: code
    });
    setCountryCodeOpen(false);
  };

  // Handle file upload
  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check file type
      const fileType = file.type;
      if (fileType !== 'application/pdf' && 
          fileType !== 'application/vnd.openxmlformats-officedocument.wordprocessingml.document') {
        setErrors({
          ...errors,
          cv: 'Only PDF and DOCX files are allowed'
        });
        return;
      }
      
      // Check file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setErrors({
          ...errors,
          cv: 'File size should not exceed 5MB'
        });
        return;
      }

      setFormData({
        ...formData,
        cv: file
      });
      setFileName(file.name);
      
      // Clear error for CV if it exists
      if (errors.cv) {
        setErrors({
          ...errors,
          cv: null
        });
      }
    }
  };

  // Validate form
  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.phoneNumber.trim()) {
      newErrors.phoneNumber = 'Phone number is required';
    } else if (!/^[0-9\s\-()]{8,15}$/.test(formData.phoneNumber)) {
      newErrors.phoneNumber = 'Phone number is invalid';
    }

    if (!formData.cv) {
      newErrors.cv = 'CV document is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (validateForm()) {
      setLoading(true);
      
      try {
        // Create form data for file upload
        const data = new FormData();
        data.append('name', formData.name);
        data.append('email', formData.email);
        data.append('phone', `${formData.phoneCountryCode}${formData.phoneNumber}`);
        data.append('cv', formData.cv);

        // Submit form to API - In a real application, you'd have the actual endpoint
        // For now, we'll simulate a successful submission after a delay
        await new Promise(resolve => setTimeout(resolve, 1500));
        
        // Simulate successful response
        // const response = await axios.post('http://localhost:5000/api/applications', data, {
        //   headers: {
        //     'Content-Type': 'multipart/form-data'
        //   }
        // });

        // Trigger success animation
        if (onSubmitSuccess) {
          onSubmitSuccess();
        }
        
        // Reset form
        setFormData({
          name: '',
          email: '',
          phoneCountryCode: '+94',
          phoneNumber: '',
          cv: null
        });
        setFileName('');
        
      } catch (error) {
        console.error('Error submitting application:', error);
        
        // Handle error
        if (error.response && error.response.data && error.response.data.message) {
          toast.error(error.response.data.message);
        } else {
          toast.error('Failed to submit application. Please try again.');
        }
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="max-w-md mx-auto text-gray-800 transition-colors">
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Name Field */}
        <div>
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="name">
            Full Name
          </label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className={`w-full px-3 py-2 border rounded-md transition-colors ${
              errors.name 
                ? 'border-red-500' 
                : 'border-gray-300 focus:border-lime-500 focus:ring focus:ring-lime-500/50'
            }`}
            placeholder="Cusherah Kugan"
          />
          {errors.name && <p className="text-red-500 text-xs mt-1">{errors.name}</p>}
        </div>

        {/* Email Field */}
        <div>
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="email">
            Email Address
          </label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className={`w-full px-3 py-2 border rounded-md transition-colors ${
              errors.email 
                ? 'border-red-500' 
                : 'border-gray-300 focus:border-lime-500 focus:ring focus:ring-lime-500/50'
            }`}
            placeholder="cusherahkugan@gmail.com"
          />
          {errors.email && <p className="text-red-500 text-xs mt-1">{errors.email}</p>}
        </div>

        {/* Phone Field with Country Code Dropdown */}
        <div>
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="phoneNumber">
            Phone Number
          </label>
          <div className="flex">
            <Popover open={countryCodeOpen} onOpenChange={setCountryCodeOpen}>
              <PopoverTrigger asChild>
                <Button
                  variant="outline"
                  role="combobox"
                  aria-expanded={countryCodeOpen}
                  className="w-[110px] justify-between border-gray-300 focus:border-lime-500 focus:ring focus:ring-lime-500/50 bg-white text-gray-700"
                >
                  {defaultCountry ? `${defaultCountry.flag} ${defaultCountry.code}` : "Select"}
                  <ChevronsUpDown className="ml-1 h-4 w-4 shrink-0 opacity-50" />
                </Button>
              </PopoverTrigger>
              <PopoverContent className="w-[250px] p-0">
                <Command>
                  <CommandInput placeholder="Search country..." />
                  <CommandEmpty>No country found.</CommandEmpty>
                  <CommandGroup className="max-h-[300px] overflow-y-auto">
                    {countryCodes.map((country) => (
                      <CommandItem
                        key={country.code}
                        value={country.code}
                        onSelect={() => handleCountryCodeSelect(country.code)}
                      >
                        <div className="flex items-center">
                          <span className="mr-2">{country.flag}</span>
                          <span>{country.name}</span>
                          <span className="ml-1 text-gray-500">{country.code}</span>
                        </div>
                        <Check
                          className={cn(
                            "ml-auto h-4 w-4",
                            formData.phoneCountryCode === country.code ? "opacity-100" : "opacity-0"
                          )}
                        />
                      </CommandItem>
                    ))}
                  </CommandGroup>
                </Command>
              </PopoverContent>
            </Popover>
            <input
              type="tel"
              id="phoneNumber"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleChange}
              className={`flex-1 px-3 py-2 border rounded-md rounded-l-none transition-colors ${
                errors.phoneNumber 
                  ? 'border-red-500' 
                  : 'border-gray-300 focus:border-lime-500 focus:ring focus:ring-lime-500/50'
              }`}
              placeholder="766741405"
            />
          </div>
          {errors.phoneNumber && <p className="text-red-500 text-xs mt-1">{errors.phoneNumber}</p>}
        </div>

        {/* CV Upload Field */}
        <div className="mt-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="cv">
            CV/Resume
          </label>
          <div className="relative border-dashed border-2 border-gray-300 rounded-lg p-4 sm:p-6 mt-1 transition-colors">
            <input
              type="file"
              id="cv"
              name="cv"
              onChange={handleFileUpload}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
              accept=".pdf,.docx,application/pdf,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
            />
            <div className="text-center">
              <svg 
                className="mx-auto h-10 w-10 text-gray-500" 
                stroke="currentColor" 
                fill="none" 
                viewBox="0 0 48 48"
              >
                <path d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4h-4m-12 4h-8" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
              </svg>
              <p className="mt-1 text-sm text-gray-600">
                {fileName ? fileName : 'Drag and drop or click to upload your CV (PDF or DOCX)'}
              </p>
              <p className="mt-1 text-xs text-gray-500">
                Max file size: 5MB
              </p>
            </div>
          </div>
          {errors.cv && <p className="text-red-500 text-xs mt-1">{errors.cv}</p>}
        </div>

        {/* Submit Button - Disabled when form is invalid */}
        <div className="flex items-center justify-center mt-6">
          <button
            type="submit"
            disabled={loading || !isFormValid}
            className={`bg-lime-500 hover:bg-lime-600 text-white font-bold py-2 px-6 rounded-md focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-lime-500 transition-colors ${
              loading || !isFormValid ? 'opacity-50 cursor-not-allowed' : ''
            }`}
          >
            {loading ? (
              <div className="flex items-center">
                <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-current" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Submitting...
              </div>
            ) : 'Submit Application'}
          </button>
        </div>
      </form>

      <ToastContainer 
        position="bottom-right"
        theme="light"
      />
    </div>
  );
};

export default ApplicationForm;
