import React, { useState, useEffect } from 'react';
import ApplicationForm from './components/ApplicationForm';
import SuccessAnimation from './components/SuccessAnimation';
import './index.css';

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [formSubmitted, setFormSubmitted] = useState(false);

  // Check user's preferred color scheme on initial load
  useEffect(() => {
    if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
      setDarkMode(true);
    }

    // Listen for changes in the color scheme preference
    window.matchMedia('(prefers-color-scheme: dark)').addEventListener('change', e => {
      setDarkMode(e.matches);
    });
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const handleFormSubmitSuccess = () => {
    setFormSubmitted(true);

    // Reset form submitted state after animation completes
    setTimeout(() => {
      setFormSubmitted(false);
    }, 6000); // Animation takes ~5 seconds, add extra second for buffer
  };

  return (
    <div className={`min-h-screen transition-colors duration-300 ease-in-out ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'} py-6 flex flex-col justify-center sm:py-12`}>
      {/* Dark mode toggle */}
      <button 
        onClick={toggleDarkMode}
        className="absolute top-4 right-4 p-2 rounded-full focus:outline-none"
        aria-label={darkMode ? "Switch to Light Mode" : "Switch to Dark Mode"}
      >
        {darkMode ? (
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-yellow-300" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
          </svg>
        ) : (
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-700" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
          </svg>
        )}
      </button>

      <div className="relative py-3 px-4 sm:px-0 w-full max-w-xl mx-auto">
        {/* Background gradient - now lime green glassmorphism */}
        <div className={`absolute inset-0 ${darkMode ? 'bg-lime-500/30' : 'bg-gradient-to-r from-lime-400 to-green-500'} shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl backdrop-blur-md`}></div>

        <div className={`relative px-4 py-8 sm:px-8 md:p-10 ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-lg sm:rounded-3xl transition-colors duration-300`}>
          <div className="max-w-md mx-auto">
            {formSubmitted ? (
              <SuccessAnimation darkMode={darkMode} />
            ) : (
              <>
                <div className="text-center">
                  <h1 className={`text-2xl sm:text-3xl font-extrabold ${darkMode ? 'text-white' : 'text-gray-900'} mb-4 sm:mb-6`}>
                    Job Application
                  </h1>
                  <p className={`${darkMode ? 'text-gray-300' : 'text-gray-600'} mb-6 sm:mb-8 text-sm sm:text-base`}>
                    Please fill out the form below to apply for a position at our company.
                  </p>
                </div>
                <ApplicationForm 
                  darkMode={darkMode} 
                  onSubmitSuccess={handleFormSubmitSuccess}
                />
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
