import React, { useEffect, useState } from 'react';

// Floating rings background animation
const FloatingRingsBackground = ({ darkMode }) => {
  const [rings, setRings] = useState([]);
  
  // Fixed positions for consistent placement
  const fixedPositions = [
    { x: 15, y: 20 },  // Top left
    { x: 50, y: 15 },  // Top center
    { x: 85, y: 25 },  // Top right
    { x: 25, y: 50 },  // Middle left
    { x: 75, y: 55 },  // Middle right
    { x: 20, y: 85 },  // Bottom left
    { x: 50, y: 80 },  // Bottom center
    { x: 80, y: 75 },  // Bottom right
  ];
  
  useEffect(() => {
    // Generate ring elements
    const generateRings = () => {
      const ringsCount = window.innerWidth < 768 ? 5 : 8;
      const newRings = [];
      
      for (let i = 0; i < ringsCount; i++) {
        // Use fixed positions
        const position = fixedPositions[i % fixedPositions.length];
        
        // Calculate properties for each ring
        const size = 150 + (i * 40) % 300; // Size between 150px and 450px
        
        // 5 times the thickness from the reference (10-25px * 5 = 50-125px)
        const thickness = (10 + (i * 2) % 15) * 5; 
        
        // Fixed but varied movement directions
        const xDirection = i % 2 === 0 ? 1 : -1;
        const yDirection = i % 3 === 0 ? 1 : -1;
        
        // Fixed but varied movement durations and distances
        const duration = 15 + (i * 4) % 30; // Animation duration between 15s and 45s
        const xDistance = 10 + (i * 3) % 30; // Move 10-40% horizontally
        const yDistance = 10 + (i * 4) % 30; // Move 10-40% vertically
        
        newRings.push({
          id: i,
          size,
          x: position.x,
          y: position.y,
          thickness,
          xDirection,
          yDirection,
          duration,
          xDistance,
          yDistance,
          color1: darkMode ? 'rgba(132, 204, 22, 0.8)' : 'rgba(132, 204, 22, 0.8)',
          color2: darkMode ? 'rgba(101, 163, 13, 0.8)' : 'rgba(16, 185, 129, 0.8)'
        });
      }
      
      setRings(newRings);
    };
    
    generateRings();
    
    // Regenerate on window resize
    const handleResize = () => {
      generateRings();
    };
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [darkMode]);

  return (
    <div className="rings-animation-container absolute inset-0 overflow-hidden z-0" style={{ pointerEvents: 'none' }}>
      {rings.map((ring) => (
        <div
          key={ring.id}
          className="absolute rounded-full"
          style={{
            width: `${ring.size}px`,
            height: `${ring.size}px`,
            border: `${ring.thickness}px solid`,
            borderColor: ring.id % 2 === 0 ? ring.color1 : ring.color2,
            background: 'transparent',
            left: `calc(${ring.x}% - ${ring.size / 2}px)`,
            top: `calc(${ring.y}% - ${ring.size / 2}px)`,
            animation: `float-${ring.id} ${ring.duration}s ease-in-out infinite`,
          }}
        />
      ))}
      
      {/* Create keyframe animations for each ring */}
      <style>
        {rings.map((ring) => `
          @keyframes float-${ring.id} {
            0%, 100% {
              transform: translate(0, 0);
            }
            50% {
              transform: translate(
                ${ring.xDirection * ring.xDistance}%, 
                ${ring.yDirection * ring.yDistance}%
              );
            }
          }
        `).join('')}
      </style>
    </div>
  );
};

const PulsatingGradient = ({ darkMode }) => {
  return (
    <div
      className={`absolute inset-0 ${
        darkMode
          ? 'bg-lime-500 opacity-30'
          : 'bg-gradient-to-r from-lime-500 to-green-500'
      } shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl`}
      style={{
        animation: darkMode ? 'none' : 'pulse 5s ease-in-out infinite'
      }}
    />
  );
};

export { FloatingRingsBackground as RotatingRingsBackground, PulsatingGradient };
