import React from 'react';

// Paper clip component for the top of the form
const PaperclipHeader = ({ darkMode }) => {
  return (
    <div className="relative w-full flex justify-center -mt-10 mb-2">
      {/* Paper clip stem */}
      <div 
        className={`w-10 h-32 rounded-t-full border-4 ${darkMode ? 'border-gray-600' : 'border-gray-400'} absolute -top-12`}
        style={{
          borderBottom: 'none',
          zIndex: 110,
          background: darkMode ? '#1F2937' : '#F3F4F6'
        }}
      ></div>
      
      {/* Paper clip circular head */}
      <div 
        className={`w-16 h-16 rounded-full border-4 ${darkMode ? 'border-gray-600' : 'border-gray-400'} absolute -top-20`}
        style={{
          zIndex: 109,
          background: darkMode ? '#1F2937' : '#F3F4F6',
          boxShadow: darkMode ? '0 4px 6px rgba(0, 0, 0, 0.3)' : '0 4px 6px rgba(0, 0, 0, 0.1)'
        }}
      ></div>
    </div>
  );
};

// Add this component to your form container:
// Right after the opening div of your form container:
{/* 
<div 
  className={`form-container relative px-4 py-6 sm:px-8 md:p-10 ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-xl sm:rounded-xl transition-all duration-300`}
  ...
>
  <PaperclipHeader darkMode={darkMode} />
  <div className="max-w-md mx-auto">
    ...
*/}

export default PaperclipHeader;
