import React, { useEffect, useState } from 'react';

// Floating rings background animation with evenly distributed positions
const FloatingRingsBackground = ({ darkMode }) => {
  const [rings, setRings] = useState([]);
  
  useEffect(() => {
    // Generate ring elements with evenly distributed positions
    const generateRings = () => {
      const isMobile = window.innerWidth < 768;
      
      // Define a grid that avoids the center area (where form would be)
      const positions = [];
      
      // Top left area
      positions.push({ x: 15, y: 15 });
      positions.push({ x: 30, y: 25 });
      
      // Top right area
      positions.push({ x: 70, y: 15 });
      positions.push({ x: 85, y: 25 });
      
      // Bottom left area
      positions.push({ x: 15, y: 75 });
      positions.push({ x: 30, y: 85 });
      
      // Bottom right area
      positions.push({ x: 70, y: 75 });
      positions.push({ x: 85, y: 85 });
      
      const ringsCount = isMobile ? 5 : 8;
      const newRings = [];
      
      for (let i = 0; i < ringsCount; i++) {
        // Get position from our form-avoiding grid
        const position = positions[i % positions.length];
        
        // Calculate other properties deterministically but varied
        const size = 150 + ((i * 37) % 300); // Size between 150px and 450px
        const thickness = 50; // Keep the same thickness as provided
        const xDirection = i % 2 === 0 ? 1 : -1;
        const yDirection = i % 3 === 0 ? 1 : -1;
        const duration = 15 + ((i * 7) % 30); // Animation between 15s and 45s
        const xDistance = 10 + ((i * 9) % 30); // Move 10-40% horizontally
        const yDistance = 10 + ((i * 13) % 30); // Move 10-40% vertically
        const delay = ((i * 5) % 20) * -1; // Deterministic delay
        const gradientAngle = (i * 45) % 360; // Unique gradient angle for each ring
        
        newRings.push({
          id: i,
          size,
          x: position.x,
          y: position.y,
          thickness,
          xDirection,
          yDirection,
          duration,
          xDistance,
          yDistance,
          delay,
          gradientAngle
        });
      }
      
      setRings(newRings);
    };
    
    generateRings();
    
    // Regenerate on window resize
    const handleResize = () => {
      generateRings();
    };
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className="rings-animation-container absolute inset-0 overflow-hidden z-0" style={{ pointerEvents: 'none' }}>
      {rings.map((ring) => (
        <div
          key={ring.id}
          className="absolute rounded-full"
          style={{
            width: `${ring.size}px`,
            height: `${ring.size}px`,
            borderWidth: `${ring.thickness}px`,
            borderStyle: 'solid',
            // Create gradient border by using an empty div with background gradient and an inner div
            background: 'transparent',
            // Use custom gradient border
            borderImage: getGradientBorder(ring.gradientAngle, darkMode),
            // Fallback color
            borderColor: darkMode ? 'rgba(132, 204, 22, 0.7)' : 'rgba(16, 185, 129, 0.7)',
            opacity: 0.4, // Slightly increased opacity for gradient visibility
            animation: `float-${ring.id} ${ring.duration}s ease-in-out infinite ${ring.delay}s`,
            // Initial position
            left: `calc(${ring.x}% - ${ring.size / 2}px)`,
            top: `calc(${ring.y}% - ${ring.size / 2}px)`,
          }}
        />
      ))}
      
      {/* Create keyframe animations for each ring */}
      <style>
        {rings.map((ring) => `
          @keyframes float-${ring.id} {
            0%, 100% {
              transform: translate(0, 0);
            }
            50% {
              transform: translate(
                ${ring.xDirection * ring.xDistance}%, 
                ${ring.yDirection * ring.yDistance}%
              );
            }
          }
        `).join('')}
      </style>
    </div>
  );
};

// Function to create gradient border styling
function getGradientBorder(angle, darkMode) {
  if (darkMode) {
    // Dark mode: gradient between lime shades
    return `linear-gradient(${angle}deg, rgba(132, 204, 22, 0.8), rgba(101, 163, 13, 0.8)) 1`;
  } else {
    // Light mode: gradient between lime and green
    return `linear-gradient(${angle}deg, rgba(132, 204, 22, 0.8), rgba(16, 185, 129, 0.8)) 1`;
  }
}

const PulsatingGradient = ({ darkMode }) => {
  return (
    <div
      className={`absolute inset-0 ${
        darkMode
          ? 'bg-lime-500 opacity-30'
          : 'bg-gradient-to-r from-lime-500 to-green-500'
      } shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl`}
      style={{
        animation: darkMode ? 'none' : 'pulse 5s ease-in-out infinite'
      }}
    />
  );
};

export { FloatingRingsBackground as RotatingRingsBackground, PulsatingGradient };
