import React, { useEffect, useRef } from 'react';

// Rotating Rings Background Component
export const RotatingRingsBackground = ({ darkMode }) => {
  const containerRef = useRef(null);
  
  useEffect(() => {
    const createRings = () => {
      if (!containerRef.current) return;
      
      // Clear any existing rings
      containerRef.current.innerHTML = '';
      
      // Container dimensions
      const containerWidth = containerRef.current.offsetWidth;
      const containerHeight = containerRef.current.offsetHeight;
      const centerX = containerWidth / 2;
      const centerY = containerHeight / 2;
      
      // Calculate diagonal length for maximum ring size
      const maxDimension = Math.sqrt(containerWidth * containerWidth + containerHeight * containerHeight);
      const maxRingSize = maxDimension * 1.2; // Slightly larger to ensure full coverage
      
      // Create rings
      const numRings = 5;
      for (let i = 0; i < numRings; i++) {
        const ring = document.createElement('div');
        const size = maxRingSize * ((i + 1) / numRings);
        const thickness = 4 + (i * 3); // Increasing thickness for outer rings
        
        ring.className = 'absolute ring';
        ring.style.width = `${size}px`;
        ring.style.height = `${size}px`;
        ring.style.borderRadius = '50%';
        ring.style.border = `${thickness}px solid`;
        // Use exact lime-500 color code for both light and dark mode to match the submit button
        ring.style.borderColor = darkMode 
          ? `rgba(132, 204, 22, ${0.3 - (i * 0.02)})`  // Lime-500 with reduced opacity for dark mode
          : `rgba(132, 204, 22, ${0.2 - (i * 0.01)})`;   // Lime-500 with reduced opacity for light mode
        
        ring.style.position = 'absolute';
        ring.style.left = `${centerX - (size / 2)}px`;
        ring.style.top = `${centerY - (size / 2)}px`;
        
        // Set different animation durations and directions
        const direction = i % 2 === 0 ? 'normal' : 'reverse';
        const duration = 20 + (i * 10); // seconds
        ring.style.animation = `spin ${duration}s linear infinite ${direction}`;
        
        // Add higher z-index and no backdrop-filter for dark mode
        ring.style.zIndex = "10";
        ring.style.backdropFilter = "none";
        
        containerRef.current.appendChild(ring);
      }
    };
    
    createRings();
    
    // Handle window resize
    window.addEventListener('resize', createRings);
    return () => {
      window.removeEventListener('resize', createRings);
    };
  }, [darkMode]);
  
  return (
    <div 
      ref={containerRef} 
      className="absolute inset-0 overflow-hidden"
      style={{ zIndex: 10 }}
    >
      {/* Rings will be added dynamically */}
      <style jsx>{`
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

// Pulsating Gradient Background Component
export const PulsatingGradient = ({ darkMode }) => {
  return (
    <div 
      className="absolute inset-0 overflow-hidden rounded-lg transition-all duration-500"
      style={{ zIndex: 90 }}
    >
      <div 
        className="absolute inset-0 gradient-animation"
        style={{
          background: darkMode 
            ? 'linear-gradient(45deg, rgba(20, 20, 30, 1) 0%, rgba(40, 40, 60, 1) 100%)' 
            : 'linear-gradient(45deg, rgba(220, 240, 220, 0.8) 0%, rgba(210, 250, 215, 0.9) 100%)',
          animation: 'gradientShift 8s ease infinite',
          backdropFilter: darkMode ? 'none' : 'blur(4px)'
        }}
      ></div>
      
      {/* Add diagonal gradient with diagonal stripes using lime-500 color */}
      <div 
        className="absolute inset-0"
        style={{
          background: darkMode
            ? 'repeating-linear-gradient(45deg, rgba(132, 204, 22, 0.25), rgba(132, 204, 22, 0.25) 10px, transparent 10px, transparent 20px)'
            : 'repeating-linear-gradient(45deg, rgba(132, 204, 22, 0.15), rgba(132, 204, 22, 0.15) 10px, transparent 10px, transparent 20px)',
          animation: 'slide 40s linear infinite',
          backdropFilter: 'none'
        }}
      ></div>
      
      <style jsx>{`
        @keyframes gradientShift {
          0%, 100% { 
            transform: scale(1); 
            opacity: 1;
          }
          50% { 
            transform: scale(1.05);
            opacity: 0.9;
          }
        }
        
        @keyframes slide {
          0% { background-position: 0 0; }
          100% { background-position: 500px 500px; }
        }
      `}</style>
    </div>
  );
};

export default { RotatingRingsBackground, PulsatingGradient };
