import React, { useState, useEffect } from 'react';

const AnimatedErrorMessage = ({ message, onClose }) => {
  const [animationState, setAnimationState] = useState('enter');
  
  useEffect(() => {
    // Start talking animation after entering
    const talkingTimer = setTimeout(() => {
      setAnimationState('talking');
    }, 600);
    
    // Auto close after 5 seconds
    const closeTimer = setTimeout(() => {
      setAnimationState('exit');
      
      // Remove component after exit animation completes
      setTimeout(() => {
        if (onClose) onClose();
      }, 500);
    }, 5000);
    
    return () => {
      clearTimeout(talkingTimer);
      clearTimeout(closeTimer);
    };
  }, [onClose]);
  
  // Generate personalized messages based on the error
  const getErrorTitle = () => {
    if (message.includes("name")) {
      return "Hey there!";
    } else if (message.includes("email")) {
      return "Oops!";
    } else if (message.includes("phone")) {
      return "Just a sec!";
    } else if (message.includes("CV")) {
      return "Almost there!";
    } else {
      return "Hmm...";
    }
  };
  
  // Get animated character expression based on error type
  const getCharacterExpression = () => {
    if (message.includes("name")) {
      return "questioning";
    } else if (message.includes("email")) {
      return "confused";
    } else if (message.includes("phone")) {
      return "thinking";
    } else if (message.includes("CV")) {
      return "pointing";
    } else {
      return "default";
    }
  };
  
  const expression = getCharacterExpression();
  
  // Get random position for floating elements
  const getRandomPosition = () => {
    return {
      top: `${Math.random() * 80 + 10}%`,
      left: `${Math.random() * 80 + 10}%`,
      size: `${Math.random() * 40 + 20}px`,
      delay: `${Math.random() * 5}s`,
      duration: `${Math.random() * 10 + 15}s`
    };
  };
  
  // Generate background elements
  const backgroundElements = Array.from({ length: 8 }, () => getRandomPosition());
  
  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 overflow-hidden">
      {/* Fullscreen background with blur and gradient */}
      <div 
        className={`absolute inset-0 transition-all duration-500 z-10`}
        style={{ 
          opacity: animationState === 'exit' ? 0 : 1,
          background: 'linear-gradient(135deg, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 100%)',
          backdropFilter: 'blur(8px)',
        }}
        onClick={() => setAnimationState('exit')}
      >
        {/* Floating background elements */}
        {backgroundElements.map((pos, index) => (
          <div 
            key={index}
            className="absolute rounded-full opacity-30"
            style={{
              top: pos.top,
              left: pos.left,
              width: pos.size,
              height: pos.size,
              background: 'radial-gradient(circle, rgba(255,255,255,0.8) 0%, rgba(255,255,255,0) 70%)',
              animation: `float ${pos.duration} ease-in-out infinite`,
              animationDelay: pos.delay,
            }}
          />
        ))}
      </div>
      
      {/* Message container */}
      <div 
        className={`relative bg-white rounded-xl p-6 shadow-2xl max-w-md w-11/12 mx-4 z-20 transition-all duration-700 transform
          ${animationState === 'enter' ? 'scale-100 translate-y-0' : ''} 
          ${animationState === 'exit' ? 'scale-90 translate-y-20 opacity-0' : ''}
          ${animationState === 'talking' ? 'scale-100' : 'scale-95'}
        `}
        style={{
          animation: animationState === 'enter' ? 'bounceIn 0.8s cubic-bezier(0.36, 0.07, 0.19, 0.97)' : '',
          boxShadow: '0 20px 60px rgba(0, 0, 0, 0.5), 0 0 40px rgba(0, 0, 0, 0.3) inset'
        }}
      >
        {/* Glowing border effect */}
        <div 
          className="absolute inset-0 rounded-xl z-0"
          style={{
            background: 'linear-gradient(135deg, #ff7e5f, #feb47b)',
            filter: 'blur(15px)',
            opacity: 0.5,
            animation: 'pulse 2s infinite'
          }}
        />
        
        {/* Close button */}
        <button 
          onClick={() => setAnimationState('exit')}
          className="absolute top-3 right-3 text-gray-500 hover:text-gray-800 z-30 transition-transform hover:rotate-90 duration-300"
          aria-label="Close"
        >
          <svg className="h-6 w-6" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M18 6L6 18M6 6l12 12" strokeLinecap="round" strokeLinejoin="round" />
          </svg>
        </button>
        
        {/* Content container */}
        <div className="relative z-20">
          {/* Avatar and message layout */}
          <div className="flex flex-col sm:flex-row items-center sm:items-start gap-4">
            {/* Avatar/Character */}
            <div 
              className={`w-32 h-32 flex-shrink-0 ${animationState === 'talking' ? 'avatar-talking' : ''}`}
              style={{
                animation: animationState === 'talking' ? 'floatAvatar 3s ease-in-out infinite' : ''
              }}
            >
              <svg viewBox="0 0 120 120" className="w-full h-full drop-shadow-lg">
                {/* Background glow */}
                <circle cx="60" cy="60" r="58" fill="url(#avatarGlow)" />
                
                {/* Head base */}
                <circle cx="60" cy="60" r="50" fill="#FFD7B5" />
                
                {/* Hair */}
                <path d="M20 40C15 25 30 15 60 15C90 15 105 25 100 40" fill="#6B46C1" />
                <path d="M16 50C16 30 20 20 30 15" stroke="#6B46C1" strokeWidth="6" strokeLinecap="round" />
                <path d="M104 50C104 30 100 20 90 15" stroke="#6B46C1" strokeWidth="6" strokeLinecap="round" />
                
                {/* Face */}
                <g className="face">
                  {/* Eyes */}
                  <g className="eyes">
                    {/* Left eye */}
                    <ellipse cx="40" cy="50" rx="8" ry={expression === "confused" ? "6" : "8"} fill="white" stroke="#333" strokeWidth="1" />
                    <circle className="pupil" cx={expression === "confused" ? "38" : "40"} cy="50" r="4" fill="#333">
                      <animate attributeName="cy" values="50;49;50;51;50" dur="3s" repeatCount="indefinite" />
                    </circle>
                    
                    {/* Right eye */}
                    <ellipse cx="80" cy="50" rx="8" ry="8" fill="white" stroke="#333" strokeWidth="1" />
                    <circle className="pupil" cx={expression === "confused" ? "82" : "80"} cy="50" r="4" fill="#333">
                      <animate attributeName="cy" values="50;49;50;51;50" dur="3s" repeatCount="indefinite" />
                    </circle>
                    
                    {/* Blinking animation */}
                    <animate 
                      xlinkHref=".eyes ellipse"
                      attributeName="ry" 
                      values="8;1;8" 
                      dur="5s"
                      begin={Math.random() * 5}
                      repeatCount="indefinite"
                    />
                  </g>
                  
                  {/* Eyebrows - expression based */}
                  {expression === "questioning" && (
                    <g>
                      <path d="M32 38C32 38 38 33 48 38" stroke="#333" strokeWidth="3" strokeLinecap="round" />
                      <path d="M72 35C72 35 82 40 88 35" stroke="#333" strokeWidth="3" strokeLinecap="round" />
                    </g>
                  )}
                  
                  {expression === "confused" && (
                    <g>
                      <path d="M32 38C32 38 38 33 48 38" stroke="#333" strokeWidth="3" strokeLinecap="round" />
                      <path d="M72 38C72 38 82 33 88 38" stroke="#333" strokeWidth="3" strokeLinecap="round" />
                    </g>
                  )}
                  
                  {expression === "thinking" && (
                    <g>
                      <path d="M32 40C32 40 38 34 48 40" stroke="#333" strokeWidth="3" strokeLinecap="round" />
                      <path d="M70 40C70 40 80 34 90 40" stroke="#333" strokeWidth="3" strokeLinecap="round" />
                    </g>
                  )}
                  
                  {expression === "pointing" || expression === "default" && (
                    <g>
                      <path d="M32 38C32 38 38 34 48 38" stroke="#333" strokeWidth="3" strokeLinecap="round" />
                      <path d="M72 38C72 38 82 34 88 38" stroke="#333" strokeWidth="3" strokeLinecap="round" />
                    </g>
                  )}
                  
                  {/* Mouth - animated talking */}
                  <g className="mouth">
                    {expression === "questioning" && (
                      <path d="M45 75C50 70 70 70 75 75" stroke="#333" strokeWidth="3" fill="#FF6B6B" strokeLinecap="round">
                        <animate attributeName="d" values="M45 75C50 70 70 70 75 75;M45 80C50 75 70 75 75 80;M45 75C50 70 70 70 75 75" dur="0.5s" repeatCount="indefinite" />
                      </path>
                    )}
                    
                    {expression === "confused" && (
                      <path d="M45 80C50 75 70 75 75 80" stroke="#333" strokeWidth="3" fill="#FF6B6B" strokeLinecap="round">
                        <animate attributeName="d" values="M45 80C50 75 70 75 75 80;M45 75C50 70 70 70 75 75;M45 80C50 75 70 75 75 80" dur="0.5s" repeatCount="indefinite" />
                      </path>
                    )}
                    
                    {expression === "thinking" && (
                      <path d="M45 77C50 77 70 77 75 77" stroke="#333" strokeWidth="3" fill="#FF6B6B" strokeLinecap="round">
                        <animate attributeName="d" values="M45 77C50 77 70 77 75 77;M45 75C50 70 70 70 75 75;M45 77C50 77 70 77 75 77" dur="0.5s" repeatCount="indefinite" />
                      </path>
                    )}
                    
                    {expression === "pointing" && (
                      <path d="M45 75C50 70 70 70 75 75" stroke="#333" strokeWidth="3" fill="#FF6B6B" strokeLinecap="round">
                        <animate attributeName="d" values="M45 75C50 70 70 70 75 75;M45 80C50 75 70 75 75 80;M45 75C50 70 70 70 75 75" dur="0.5s" repeatCount="indefinite" />
                      </path>
                    )}
                    
                    {expression === "default" && (
                      <path d="M45 75C50 70 70 70 75 75" stroke="#333" strokeWidth="3" fill="#FF6B6B" strokeLinecap="round">
                        <animate attributeName="d" values="M45 75C50 70 70 70 75 75;M45 80C50 75 70 75 75 80;M45 75C50 70 70 70 75 75" dur="0.5s" repeatCount="indefinite" />
                      </path>
                    )}
                  </g>
                  
                  {/* Cheeks */}
                  <circle cx="30" cy="65" r="10" fill="#FFCBD1" fillOpacity="0.6" />
                  <circle cx="90" cy="65" r="10" fill="#FFCBD1" fillOpacity="0.6" />
                </g>
                
                {/* Special features based on expression */}
                {expression === "thinking" && (
                  <g className="thinking-bubbles">
                    <circle cx="100" cy="20" r="8" fill="white" fillOpacity="0.9">
                      <animate attributeName="r" values="8;10;8" dur="3s" repeatCount="indefinite" />
                    </circle>
                    <circle cx="85" cy="15" r="5" fill="white" fillOpacity="0.7">
                      <animate attributeName="r" values="5;6;5" dur="3s" repeatCount="indefinite" />
                    </circle>
                    <circle cx="75" cy="10" r="3" fill="white" fillOpacity="0.5">
                      <animate attributeName="r" values="3;4;3" dur="3s" repeatCount="indefinite" />
                    </circle>
                  </g>
                )}
                
                {expression === "pointing" && (
                  <g className="pointing-hand" transform="translate(85, 85) rotate(20)">
                    <path d="M0 0C-5 -5 -5 -20 0 -25C5 -30 15 -15 15 -15L20 -25C20 -25 25 -20 20 -15L25 -20C25 -20 30 -15 25 -10L30 -15C30 -15 35 -10 30 -5L35 -10C35 -10 40 -5 35 0C30 5 15 10 10 10C5 10 5 5 0 0Z" fill="#FFD7B5" stroke="#333" strokeWidth="1" />
                  </g>
                )}
                
                {/* Define gradients and filters */}
                <defs>
                  <radialGradient id="avatarGlow" cx="50%" cy="50%" r="50%" fx="50%" fy="50%">
                    <stop offset="0%" stopColor="#7C3AED" stopOpacity="0.6" />
                    <stop offset="100%" stopColor="#7C3AED" stopOpacity="0" />
                  </radialGradient>
                </defs>
              </svg>
            </div>
            
            {/* Message content */}
            <div className="flex-1">
              <div 
                className="relative bg-gradient-to-br from-purple-100 to-white rounded-2xl p-4 shadow-lg"
                style={{
                  animation: animationState === 'talking' ? 'pulse 2s infinite' : ''
                }}
              >
                {/* Message bubble pointer - diagonal style */}
                <div
                  className="hidden sm:block absolute -left-6 top-6 w-6 h-6"
                  style={{
                    background: 'linear-gradient(135deg, #EDE9FE 0%, #F5F3FF 100%)',
                    clipPath: 'polygon(100% 0, 0 100%, 100% 100%)',
                    transformOrigin: 'bottom right',
                    transform: 'rotate(35deg)'
                  }}
                />
                
                {/* Mobile-only top pointer */}
                <div
                  className="sm:hidden absolute top-0 left-1/2 w-6 h-6 -translate-x-1/2 -translate-y-3"
                  style={{
                    background: 'linear-gradient(135deg, #EDE9FE 0%, #F5F3FF 100%)',
                    clipPath: 'polygon(50% 0, 0 100%, 100% 100%)',
                  }}
                />
                
                {/* Title with gradient */}
                <h3 
                  className="text-xl font-bold mb-2"
                  style={{
                    background: 'linear-gradient(90deg, #7C3AED, #EC4899)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                  }}
                >
                  {getErrorTitle()}
                </h3>
                
                {/* Message with animation */}
                <p className="text-gray-700 leading-relaxed">{message}</p>
                
                {/* Animated underline */}
                <div 
                  className="w-full h-0.5 mt-3 relative overflow-hidden"
                  style={{ background: 'rgba(124, 58, 237, 0.2)' }}
                >
                  <div 
                    className="absolute top-0 left-0 h-full bg-purple-600"
                    style={{
                      width: '30%',
                      animation: 'moveWidth 2s infinite',
                    }}
                  />
                </div>
              </div>
              
              {/* Typing animation dots */}
              {animationState === 'talking' && (
                <div className="flex space-x-1 mt-3 ml-2">
                  <div className="w-3 h-3 bg-purple-500 rounded-full animate-bounce" />
                  <div className="w-3 h-3 bg-purple-500 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }} />
                  <div className="w-3 h-3 bg-purple-500 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }} />
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      
      {/* Keyframe animations */}
      <style jsx>{`
        @keyframes bounceIn {
          0% { transform: scale(0.3); opacity: 0; }
          40% { transform: scale(1.1); }
          60% { transform: scale(0.9); }
          80% { transform: scale(1.03); }
          100% { transform: scale(1); opacity: 1; }
        }
        
        @keyframes pulse {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.02); }
        }
        
        @keyframes floatAvatar {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        
        @keyframes float {
          0%, 100% { transform: translateY(0) translateX(0); }
          25% { transform: translateY(-15px) translateX(10px); }
          50% { transform: translateY(-25px) translateX(-10px); }
          75% { transform: translateY(-15px) translateX(-5px); }
        }
        
        @keyframes moveWidth {
          0% { transform: translateX(-100%); }
          50%, 100% { transform: translateX(300%); }
        }
      `}</style>
    </div>
  );
};

export default AnimatedErrorMessage;
