import React, { useState, useEffect } from 'react';
import ApplicationForm from './components/ApplicationForm';
import SuccessAnimation from './components/SuccessAnimation';
import { AnimatedBackground, JobAnimationBackground, PulsatingGradient } from './components/BackgroundAnimation';
import './index.css';

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [formSubmitted, setFormSubmitted] = useState(false);

  // Check user's preferred color scheme on initial load - but default to light mode
  useEffect(() => {
    // Default to light mode - removed the system preference check
    setDarkMode(false);

    // Listen for changes in the color scheme preference
    const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');
    const handleChange = (e) => {
      // Optional: if you want to still listen to system changes
      // setDarkMode(e.matches);
    };

    mediaQuery.addEventListener('change', handleChange);
    return () => {
      mediaQuery.removeEventListener('change', handleChange);
    };
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const handleFormSubmitSuccess = () => {
    setFormSubmitted(true);

    // Reset form submitted state after animation completes
    setTimeout(() => {
      setFormSubmitted(false);
    }, 6000); // Animation takes ~5 seconds, add extra second for buffer
  };

  return (
    <div className={`min-h-screen transition-colors duration-500 ease-in-out ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'} py-6 flex flex-col justify-center sm:py-12 overflow-hidden`}>
      {/* Animated backgrounds */}
      <AnimatedBackground darkMode={darkMode} />

      {/* Dark mode toggle switch with hover animation */}
      <div className="absolute top-4 right-4 flex items-center space-x-2 bg-white dark:bg-gray-800 p-2 rounded-full shadow-md z-30 transition-all duration-300 hover:shadow-lg">
        <div className="flex items-center justify-center w-6 h-6 text-gray-700 dark:text-yellow-300">
          {darkMode ? (
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 transform transition-transform hover:rotate-45" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
            </svg>
          ) : (
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 transform transition-transform hover:rotate-12" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
            </svg>
          )}
        </div>
        {/* Animated toggle switch */}
        <div 
          onClick={toggleDarkMode}
          className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors focus:outline-none ${darkMode ? 'bg-lime-500' : 'bg-gray-300'}`}
        >
          <span 
            className={`inline-block h-4 w-4 transform rounded-full bg-white transition-all duration-300 ${darkMode ? 'translate-x-6' : 'translate-x-1'}`} 
          />
        </div>
      </div>

      <div className="relative py-3 px-4 sm:px-0 w-full max-w-xl mx-auto">
        {/* Background gradient that changes with dark mode */}
        <PulsatingGradient darkMode={darkMode} />

        {/* Background animated job icons */}
        <JobAnimationBackground darkMode={darkMode} />

        {/* Always white form background in both modes */}
        <div className="relative px-4 py-8 sm:px-8 md:p-10 bg-white shadow-lg sm:rounded-3xl transition-colors duration-300 z-10">
          <div className="max-w-md mx-auto">
            {formSubmitted ? (
              <SuccessAnimation darkMode={darkMode} />
            ) : (
              <>
                <div className="text-center">
                  <h1 className="text-2xl sm:text-3xl font-extrabold text-gray-900 mb-4 sm:mb-6 animate-bounce-in">
                    Job Application
                  </h1>
                  <p className="text-gray-600 mb-6 sm:mb-8 text-sm sm:text-base animate-bounce-in" style={{ animationDelay: '0.2s' }}>
                    Please fill out the form below to apply for a position at our company.
                  </p>
                </div>
                <ApplicationForm
                  darkMode={darkMode}
                  onSubmitSuccess={handleFormSubmitSuccess}
                />
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
