import React, { useState } from 'react';

// Demo component showing the envelope animation
const DemoEnvelopeAnimation = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [animationStage, setAnimationStage] = useState(1); // 1: form, 2: folding, 3: envelope, 4: sending
  const [showDemo, setShowDemo] = useState(false);

  // Styled components for the demo
  const Button = ({ children, primary, onClick }) => (
    <button
      onClick={onClick}
      className={`px-4 py-2 rounded-md font-medium transition-all duration-300 ${
        primary 
          ? 'bg-lime-500 hover:bg-lime-600 text-white' 
          : darkMode 
            ? 'bg-gray-700 hover:bg-gray-600 text-white' 
            : 'bg-gray-200 hover:bg-gray-300 text-gray-800'
      }`}
    >
      {children}
    </button>
  );

  // Advance the animation
  const advanceAnimation = () => {
    if (animationStage < 4) {
      setAnimationStage(animationStage + 1);
    } else {
      // Reset animation
      setAnimationStage(1);
    }
  };

  // Toggle the demo
  const toggleDemo = () => {
    setShowDemo(!showDemo);
    setAnimationStage(1);
  };

  // Gradient envelope background colors
  const envelopeGradient = darkMode 
    ? 'from-lime-700 to-green-900' 
    : 'from-lime-500 to-green-600';

  return (
    <div className={`min-h-screen ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-800'}`}>
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-2xl font-bold">Application Form Animation</h1>
          
          <div className="flex space-x-4">
            <button
              onClick={() => setDarkMode(!darkMode)}
              className={`p-2 rounded-full ${darkMode ? 'bg-gray-700' : 'bg-gray-200'}`}
            >
              {darkMode ? (
                <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                </svg>
              ) : (
                <svg className="w-6 h-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                </svg>
              )}
            </button>
            
            <Button primary onClick={toggleDemo}>
              {showDemo ? 'Hide Demo' : 'Show Animation Demo'}
            </Button>
          </div>
        </div>
        
        {showDemo ? (
          <div className="max-w-lg mx-auto">
            <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6 mb-6">
              <h2 className="text-xl font-bold mb-4">Form to Envelope Animation</h2>
              <p className="mb-4">
                This demo shows the animation when a form is submitted:
              </p>
              <div className="flex space-x-3 mb-2">
                {[1, 2, 3, 4].map((stage) => (
                  <div 
                    key={stage}
                    className={`h-2 flex-1 rounded-full ${
                      stage === animationStage
                        ? 'bg-lime-500'
                        : stage < animationStage
                          ? 'bg-lime-300'
                          : darkMode ? 'bg-gray-700' : 'bg-gray-200'
                    }`}
                  ></div>
                ))}
              </div>
              <div className="flex justify-between text-sm mb-6">
                <span>Form</span>
                <span>Folding</span>
                <span>Envelope</span>
                <span>Sent</span>
              </div>
              
              <div className="relative w-full h-80 border rounded-lg overflow-hidden">
                {/* Form paper */}
                <div className={`absolute bg-white shadow-xl rounded-lg p-4 transform transition-all duration-1000
                  ${animationStage === 1 ? 'scale-100' : ''}
                  ${animationStage === 2 ? 'scale-90 rotate-6' : ''}
                  ${animationStage === 3 ? 'scale-75 rotate-0' : ''}
                  ${animationStage >= 4 ? 'scale-75 -translate-x-full opacity-0' : ''}
                `} style={{
                  top: '50%',
                  left: '50%',
                  width: '80%',
                  transform: `translate(-50%, -50%) ${
                    animationStage === 1 ? 'scale(1)' :
                    animationStage === 2 ? 'scale(0.9) rotate(6deg)' :
                    animationStage === 3 ? 'scale(0.75) rotate(0)' :
                    'scale(0.75) translateX(-200%) translateY(-50%)'
                  }`,
                  transitionTimingFunction: animationStage >= 4 ? 'cubic-bezier(0.66, 0, 0.34, 1)' : 'ease-in-out',
                  transitionDuration: animationStage >= 4 ? '1.5s' : '1s',
                }}>
                  {/* Form content */}
                  <div className={`space-y-2 transition-all duration-1000 origin-top ${
                    animationStage === 2 ? 'scale-y-50 translate-y-12' : 
                    animationStage >= 3 ? 'scale-y-0 translate-y-24' : ''
                  }`}>
                    <div className="h-4 bg-gray-200 rounded w-1/2"></div>
                    <div className="h-6 bg-gray-100 rounded w-full"></div>
                    <div className="h-4 bg-gray-200 rounded w-1/3"></div>
                    <div className="h-6 bg-gray-100 rounded w-full"></div>
                    <div className="h-4 bg-gray-200 rounded w-1/4"></div>
                    <div className="h-6 bg-gray-100 rounded w-full"></div>
                    <div className="h-4 bg-gray-200 rounded w-2/5"></div>
                    <div className="h-16 bg-gray-100 rounded w-full"></div>
                    <div className="h-8 bg-lime-500 rounded w-1/3 mx-auto"></div>
                  </div>
                  
                  {/* Folding animation */}
                  <div className={`absolute inset-x-0 top-1/2 h-1/2 bg-white rounded-b-lg transition-all duration-700 
                    ${animationStage >= 2 ? 'rotate-x-180 origin-top' : 'opacity-0'}
                  `} style={{
                    transform: animationStage >= 2 ? 'rotateX(180deg)' : '',
                    transformOrigin: 'top',
                  }}></div>
                  
                  <div className={`absolute inset-x-0 bottom-0 h-1/2 bg-gray-100 rounded-b-lg transition-all duration-700 delay-300
                    ${animationStage >= 2 ? 'origin-top' : 'opacity-0'}
                    ${animationStage >= 3 ? 'rotate-x-180' : ''}
                  `} style={{
                    transformOrigin: 'top',
                    transform: animationStage >= 3 ? 'rotateX(180deg)' : '',
                  }}></div>
                </div>
                
                {/* Envelope */}
                <div className={`absolute bg-gradient-to-br ${envelopeGradient} rounded-lg shadow-2xl transform transition-all duration-1500
                  ${animationStage < 3 ? 'opacity-0 scale-90' : 'opacity-100 scale-100'}
                  ${animationStage >= 4 ? '-translate-x-full' : ''}
                `} style={{
                  top: '50%',
                  left: '50%',
                  width: '70%',
                  height: '40%',
                  transform: `translate(-50%, -50%) ${
                    animationStage < 3 ? 'scale(0.9)' :
                    animationStage >= 4 ? 'translateX(-200%)' : 'scale(1)'
                  }`,
                  transitionTimingFunction: animationStage >= 4 ? 'cubic-bezier(0.66, 0, 0.34, 1)' : 'ease-in-out',
                  transitionDuration: animationStage >= 4 ? '1.5s' : '1s',
                }}>
                  {/* Envelope flap */}
                  <div className={`absolute inset-x-0 -top-1/2 h-1/2 bg-gradient-to-b ${envelopeGradient} rounded-t-lg origin-bottom transition-all duration-500
                    ${animationStage === 3 ? 'rotate-x-180' : 'rotate-x-0'}
                  `} style={{
                    clipPath: 'polygon(0 100%, 50% 30%, 100% 100%)',
                    zIndex: 1,
                    transform: animationStage === 3 ? 'rotateX(180deg)' : 'rotateX(0)',
                    transformOrigin: 'bottom',
                  }}></div>
                  
                  {/* Envelope content */}
                  <div className="absolute inset-0 flex items-center justify-center">
                    <svg className="w-10 h-10 text-white opacity-80" fill="currentColor" viewBox="0 0 20 20">
                      <path d="M2.003 5.884L10 9.882l7.997-3.998A2 2 0 0016 4H4a2 2 0 00-1.997 1.884z"></path>
                      <path d="M18 8.118l-8 4-8-4V14a2 2 0 002 2h12a2 2 0 002-2V8.118z"></path>
                    </svg>
                  </div>
                </div>
                
                {/* Success message */}
                <div className={`absolute inset-0 flex items-center justify-center transition-opacity duration-1000
                  ${animationStage >= 4 ? 'opacity-100 delay-500' : 'opacity-0'}
                `}>
                  <div className="text-center">
                    <svg className="w-16 h-16 mx-auto text-lime-500 mb-4" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14" strokeLinecap="round" strokeLinejoin="round" />
                      <path d="M22 4L12 14.01l-3-3" strokeLinecap="round" strokeLinejoin="round" />
                    </svg>
                    <h3 className="text-xl font-bold mb-2">Application Submitted!</h3>
                    <p className={`${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
                      Thank you for your application.
                    </p>
                  </div>
                </div>
              </div>
              
              <div className="mt-6 flex justify-center">
                <Button primary onClick={advanceAnimation}>
                  {animationStage >= 4 ? 'Reset Animation' : 'Next Step'}
                </Button>
              </div>
            </div>
          </div>
        ) : (
          <div className="max-w-lg mx-auto bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6">
            <div className="text-center mb-6">
              <h2 className="text-xl font-bold mb-2">Application Form with Animation</h2>
              <p className={`${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
                When a user submits the application form, it folds into an envelope and flies away with a success message.
              </p>
            </div>
            
            <div className="space-y-6">
              <div className="flex items-center">
                <div className="flex-shrink-0 w-12 h-12 bg-lime-100 dark:bg-lime-900 rounded-full flex items-center justify-center">
                  <svg className="w-6 h-6 text-lime-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                </div>
                <div className="ml-4">
                  <h3 className="font-medium">Form Validation</h3>
                  <p className="text-sm text-gray-500 dark:text-gray-400">User-friendly error handling with gentle animations</p>
                </div>
              </div>
              
              <div className="flex items-center">
                <div className="flex-shrink-0 w-12 h-12 bg-lime-100 dark:bg-lime-900 rounded-full flex items-center justify-center">
                  <svg className="w-6 h-6 text-lime-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 15a4 4 0 004 4h9a5 5 0 10-.1-9.999 5.002 5.002 0 10-9.78 2.096A4.001 4.001 0 003 15z" />
                  </svg>
                </div>
                <div className="ml-4">
                  <h3 className="font-medium">Smooth Animations</h3>
                  <p className="text-sm text-gray-500 dark:text-gray-400">Paper folding effect with envelope transition</p>
                </div>
              </div>
              
              <div className="flex items-center">
                <div className="flex-shrink-0 w-12 h-12 bg-lime-100 dark:bg-lime-900 rounded-full flex items-center justify-center">
                  <svg className="w-6 h-6 text-lime-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                  </svg>
                </div>
                <div className="ml-4">
                  <h3 className="font-medium">Success Feedback</h3>
                  <p className="text-sm text-gray-500 dark:text-gray-400">Clear confirmation that the application was submitted</p>
                </div>
              </div>
            </div>
            
            <div className="mt-8 text-center">
              <Button primary onClick={toggleDemo}>View Animation Demo</Button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default DemoEnvelopeAnimation;
