import React, { useEffect, useState } from 'react';

// Floating rings background animation
const FloatingRingsBackground = ({ darkMode }) => {
  const [rings, setRings] = useState([]);
  
  useEffect(() => {
    // Generate random ring elements
    const generateRings = () => {
      const isMobile = window.innerWidth < 768;
      const ringsCount = isMobile ? 5 : 8; // Fewer rings on mobile
      const newRings = [];
      
      for (let i = 0; i < ringsCount; i++) {
        // Calculate random properties for each ring - smaller size range for mobile
        const size = isMobile 
          ? 80 + Math.random() * 150 // Size between 80px and 230px for mobile
          : 150 + Math.random() * 300; // Size between 150px and 450px for desktop
          
        const xPos = Math.random() * 100; // Initial position between 0% and 100%
        const yPos = Math.random() * 100;
        
        // Thinner rings for mobile
        const thickness = isMobile
          ? 5 + Math.random() * 15 // Thickness between 5px and 20px for mobile
          : 10 + Math.random() * 30; // Thickness between 10px and 40px for desktop
          
        // Random movement direction
        const xDirection = Math.random() > 0.5 ? 1 : -1;
        const yDirection = Math.random() > 0.5 ? 1 : -1;
        
        // Faster movement on mobile for better visual effect
        const duration = isMobile
          ? 10 + Math.random() * 20 // Animation duration between 10s and 30s for mobile
          : 15 + Math.random() * 30; // Animation duration between 15s and 45s for desktop
          
        // Reduced movement distance on mobile
        const xDistance = isMobile
          ? 5 + Math.random() * 20 // Move 5-25% horizontally on mobile
          : 10 + Math.random() * 30; // Move 10-40% horizontally on desktop
          
        const yDistance = isMobile
          ? 5 + Math.random() * 20 // Move 5-25% vertically on mobile
          : 10 + Math.random() * 30; // Move 10-40% vertically on desktop
          
        const delay = Math.random() * -20; // Random delay
        
        newRings.push({
          id: i,
          size,
          x: xPos,
          y: yPos,
          thickness,
          xDirection,
          yDirection,
          duration,
          xDistance,
          yDistance,
          delay
        });
      }
      
      setRings(newRings);
    };
    
    generateRings();
    
    // Regenerate on window resize
    const handleResize = () => {
      generateRings();
    };
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className="rings-animation-container absolute inset-0 overflow-hidden z-0" style={{ pointerEvents: 'none' }}>
      {rings.map((ring) => (
        <div
          key={ring.id}
          className="absolute rounded-full border-2 opacity-30"
          style={{
            width: `${ring.size}px`,
            height: `${ring.size}px`,
            borderWidth: `${ring.thickness}px`,
            borderColor: darkMode 
              ? 'rgb(132, 204, 22)' // Lime-500 for dark mode
              : `rgba(${Math.random() > 0.5 ? '132, 204, 22' : '16, 185, 129'})`, // Random between lime-500 and green-500
            borderStyle: 'solid',
            animation: `float-${ring.id} ${ring.duration}s ease-in-out infinite ${ring.delay}s`,
            // Initial position
            left: `calc(${ring.x}% - ${ring.size / 2}px)`,
            top: `calc(${ring.y}% - ${ring.size / 2}px)`,
          }}
        />
      ))}
      
      {/* Create keyframe animations for each ring */}
      <style>
        {rings.map((ring) => `
          @keyframes float-${ring.id} {
            0%, 100% {
              transform: translate(0, 0);
            }
            50% {
              transform: translate(
                ${ring.xDirection * ring.xDistance}%, 
                ${ring.yDirection * ring.yDistance}%
              );
            }
          }
        `).join('')}
      </style>
    </div>
  );
};

const PulsatingGradient = ({ darkMode }) => {
  return (
    <div
      className={`absolute inset-0 ${
        darkMode
          ? 'bg-gradient-to-r from-lime-500 to-green-500'
          : 'bg-gradient-to-r from-lime-500 to-green-500'
      } shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl`}
      style={{
        animation: darkMode ? 'pulse 5s ease-in-out infinite' : 'pulse 5s ease-in-out infinite'
      }}
    />
  );
};

export { FloatingRingsBackground as RotatingRingsBackground, PulsatingGradient };
