import React, { useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ApplicationForm = ({ darkMode, onSubmitSuccess }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    cv: null
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [fileName, setFileName] = useState('');

  // Handle form field changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    
    // Clear error for this field if it exists
    if (errors[e.target.name]) {
      setErrors({
        ...errors,
        [e.target.name]: null
      });
    }
  };

  // Handle file upload
  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check file type
      const fileType = file.type;
      if (fileType !== 'application/pdf' && 
          fileType !== 'application/vnd.openxmlformats-officedocument.wordprocessingml.document') {
        setErrors({
          ...errors,
          cv: 'Only PDF and DOCX files are allowed'
        });
        return;
      }
      
      // Check file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setErrors({
          ...errors,
          cv: 'File size should not exceed 5MB'
        });
        return;
      }

      setFormData({
        ...formData,
        cv: file
      });
      setFileName(file.name);
      
      // Clear error for CV if it exists
      if (errors.cv) {
        setErrors({
          ...errors,
          cv: null
        });
      }
    }
  };

  // Validate form
  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.phone.trim()) {
      newErrors.phone = 'Phone number is required';
    } else if (!/^\+?[0-9\s\-()]{8,20}$/.test(formData.phone)) {
      newErrors.phone = 'Phone number is invalid';
    }

    if (!formData.cv) {
      newErrors.cv = 'CV document is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (validateForm()) {
      setLoading(true);
      
      try {
        // Create form data for file upload
        const data = new FormData();
        data.append('name', formData.name);
        data.append('email', formData.email);
        data.append('phone', formData.phone);
        data.append('cv', formData.cv);

        // Submit form to API - In a real application, you'd have the actual endpoint
        // For now, we'll simulate a successful submission after a delay
        await new Promise(resolve => setTimeout(resolve, 1500));
        
        // Simulate successful response
        // const response = await axios.post('http://localhost:5000/api/applications', data, {
        //   headers: {
        //     'Content-Type': 'multipart/form-data'
        //   }
        // });

        // Trigger success animation
        if (onSubmitSuccess) {
          onSubmitSuccess();
        }
        
        // Reset form
        setFormData({
          name: '',
          email: '',
          phone: '',
          cv: null
        });
        setFileName('');
        
      } catch (error) {
        console.error('Error submitting application:', error);
        
        // Handle error
        if (error.response && error.response.data && error.response.data.message) {
          toast.error(error.response.data.message);
        } else {
          toast.error('Failed to submit application. Please try again.');
        }
      } finally {
        setLoading(false);
      }
    }
  };

  // Dynamic class assignments based on dark mode
  const inputClass = (fieldName) => `w-full px-3 py-2 border rounded-md transition-colors ${
    errors[fieldName] 
      ? 'border-red-500' 
      : darkMode 
        ? 'border-gray-600 bg-gray-700 text-white focus:border-lime-400 focus:ring focus:ring-lime-400/50' 
        : 'border-gray-300 focus:border-lime-500 focus:ring focus:ring-lime-500/50'
  }`;

  const labelClass = `block ${darkMode ? 'text-gray-200' : 'text-gray-700'} text-sm font-bold mb-2`;
  
  const buttonClass = `${
    darkMode 
      ? 'bg-lime-500 hover:bg-lime-600 text-gray-900' 
      : 'bg-lime-500 hover:bg-lime-600 text-white'
  } font-bold py-2 px-6 rounded-md focus:outline-none focus:ring-2 focus:ring-offset-2 ${
    darkMode ? 'focus:ring-lime-400 focus:ring-offset-gray-800' : 'focus:ring-lime-500'
  } transition-colors ${loading ? 'opacity-50 cursor-not-allowed' : ''}`;

  return (
    <div className={`max-w-md mx-auto ${darkMode ? 'text-gray-200' : 'text-gray-800'} transition-colors`}>
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Name Field */}
        <div>
          <label className={labelClass} htmlFor="name">
            Full Name
          </label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className={inputClass('name')}
            placeholder="Cusherah Kugan"
          />
          {errors.name && <p className="text-red-500 text-xs mt-1">{errors.name}</p>}
        </div>

        {/* Email Field */}
        <div>
          <label className={labelClass} htmlFor="email">
            Email Address
          </label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className={inputClass('email')}
            placeholder="cusherahkugan@gmail.com"
          />
          {errors.email && <p className="text-red-500 text-xs mt-1">{errors.email}</p>}
        </div>

        {/* Phone Field */}
        <div>
          <label className={labelClass} htmlFor="phone">
            Phone Number
          </label>
          <input
            type="tel"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            className={inputClass('phone')}
            placeholder="+94 766741405"
          />
          {errors.phone && <p className="text-red-500 text-xs mt-1">{errors.phone}</p>}
        </div>

        {/* CV Upload Field */}
        <div className="mt-4">
          <label className={labelClass} htmlFor="cv">
            CV/Resume
          </label>
          <div className={`relative border-dashed border-2 ${
            darkMode ? 'border-gray-600' : 'border-gray-300'
          } rounded-lg p-4 sm:p-6 mt-1 transition-colors`}>
            <input
              type="file"
              id="cv"
              name="cv"
              onChange={handleFileUpload}
              className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
              accept=".pdf,.docx,application/pdf,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
            />
            <div className="text-center">
              <svg 
                className={`mx-auto h-10 w-10 ${darkMode ? 'text-gray-400' : 'text-gray-500'}`} 
                stroke="currentColor" 
                fill="none" 
                viewBox="0 0 48 48"
              >
                <path d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4h-4m-12 4h-8" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
              </svg>
              <p className={`mt-1 text-sm ${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
                {fileName ? fileName : 'Drag and drop or click to upload your CV (PDF or DOCX)'}
              </p>
              <p className={`mt-1 text-xs ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                Max file size: 5MB
              </p>
            </div>
          </div>
          {errors.cv && <p className="text-red-500 text-xs mt-1">{errors.cv}</p>}
        </div>

        {/* Submit Button */}
        <div className="flex items-center justify-center mt-6">
          <button
            type="submit"
            disabled={loading}
            className={buttonClass}
          >
            {loading ? (
              <div className="flex items-center">
                <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-current" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Submitting...
              </div>
            ) : 'Submit Application'}
          </button>
        </div>
      </form>

      <ToastContainer 
        position="bottom-right"
        theme={darkMode ? 'dark' : 'light'}
      />
    </div>
  );
};

export default ApplicationForm;
